css:
@-moz-document domain("moviepilot.de") {
body, #content, .cinemas-title, #mp-content{
    background: #151515;/*url("http://i.imgur.com/NQpDEDt.jpg") 050E1B*/
    color: #727272;
}

.event .h3 .kicker { 
	color: #C57B17;
}

h4, .h4{
	color: #FFFFFF;
}

.recommendations .tabs, .content #tabs{
	margin-bottom: 20px;
}

.main p, .sidebar p, .main h1, .article--teaser{
	color: #9c9c9c;
}

ul.yellow_tabs, .functionbar{
	background-image: -webkit-gradient(linear, left bottom, left top, color-stop(0, #FCA733), color-stop(0.9, #B46E0F));
}

ul.tabs, ul.tabbed{
	box-shadow: -1px 1px 0 #151515;
}

.lists--toplist--list-item-link{
	color: #FFFFFF;
}

.content-with-sidebar, .content-without-sidebar, .layout--wrapped-content,
.recommendations-dvd, .recommendations-vod, .recommendations-cinema, .recommendations-tv, #header_sub_bar, .movie_images_m_box, .movie_images_m_box .moviedetail_image_m_description, .box, .table-with-involvement a
{
	background: #1E1E1E;
    border-bottom: none;
    margin-top: -17px;
}

#header_sub_bar .social-follow {
	display: none;
}

#header_sub_bar {
	border-radius: 15px;
    margin: 5px auto 0;
}

.banner--vdt-bar{
	margin: -50px -23px 0 1px;
}

.content-with-sidebar #sidebar, .content-without-sidebar #sidebar{
	border-left: 2px solid #707070 ;
}

#header #header-content {
	background: -webkit-gradient(linear,0 0,0 100%,from(#585858),to(#1E1E1E));    
    border-bottom-left-radius: 15px;
    border-bottom-right-radius: 15px;
}

.is-compabilities .lists--toplist--list{
	border: #cdcdcd;
    border-top: 1px solid #cdcdcd;
}

.widget{
    overflow-x: hidden !important;
    overflow-y: scroll;
	box-shadow: none;
    border-top: 2px solid #707070;
    border-bottom: 2px solid #707070;
}

#header_sub_bar .header_top_links span,
.filter_item
{
	color: #3070C3;
    font-weight: bold;
}

#header_sub_bar .header_top_links a {
	color: #FFFFFF;
}

a{
	color: #3070C3;
}

#forums-ctrl #main{
	margin: 0 0 30px 24px;
}

.list-showtimes a{
	background: #1E1E1E;
}

body, #content, .cinemas-title, #mp-content{
	color: #F9B516;
}


/*Filmseiten*/

.fds_movie_header h1, .filter--content--option, h1, .h1,
.season .seasoncommunitycount .quite,
.season .seasoncommunitycount .huge
{
	color: #C57B17;
}

#tabs{
	background-color: #151515;
}

.movie_actionbar{
	background: #E4E4E4;
}

.consumption_box{
	display: none;
}

.icon_26 span, .movie.rating-landscape .details p{
	color: #9C9C9C;
}

.comments, #footer, .sidebar_poster_slider, .news-sidebar .full_sidebar_news_teaser .news_teaser_content{
    margin-top: 0 !important;
	background: #1E1E1E;
}

.read-more .label, .read-less .label, .breadcrumbs, .news-sidebar .full_sidebar_news_teaser .news_teaser_content .strong a{
	color: #C2C2C2;
    text-shadow: none;
}

.call_to_action_text{
	color: #2169C6 !important;
    font-size: 14px;
}

.comment--content-comment{
	background: #2E2E2E;
    border: 1px solid #606060;
}

h2, .h2, legend{
	color: #FFFFFF;
}

.person_img_portrait .h3, .comment--form--user--username{
	color: #C3C3C3 !important;
}

.comment--form--container{
	border: 1px solid #606060;
}

.comment--form--actions{
	background: #2E2E2E;
    border-top: 1px solid #606060;
}

.comment--form--body{
    color: #9c9c9c;
	background: #2E2E2E;
    border-top: 1px solid #606060;
    border-left: 1px solid #606060;
	width: 99.5%;
}

#before_content_block_with_tabs_and_sidebar h1, .cinemas_title h1{
	text-shadow: none;
}
}
