css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2ch.hk"), domain("2ch.pm") {
 @media only screen and (min-width: 480px) {
  
  /* MAKABA DEFAULT */
	body {padding: 0; margin: 0; font-size: 0.8em;}
	.prerekl-hr, .rekl  {	display: none;}
	.desktop {	display: none !important;}
	.mobile {display: block !important;	clear: both !important;	}
	.logo {	font-size: 1.5em !important;}
	.passcode-banner-mob {display: block !important;}
	#postform .mail input[type="text"], #postform td select#anoniconsselectlist, #postform td #shampoo, #postform td input#subject  {
		width: 100% !important;
		min-width: 233px;
	}
	#postform .name #name, #postform td input#subject {	margin-bottom: 2px;	}
	.post-wrapper {	margin: 4px 0;padding: 0 10px; min-height: 75px !important;}
	.post .post-details {background: #D1D1D1; padding: 5px;}
	.post .images {margin-bottom: 5px;}
	.post .posttime-reflink {float: right;}
	blockquote.post-message {margin:1em;}
	.file-attr {font-size:1em;}
	.file-attr .filesize {font-size:1.1em;}
	.oppost-options-mob .mess-post-mob {float: left;}
	.oppost-options-mob .hide-view { float: right;}
	.posts  hr {margin-top: 30px !important;margin-bottom: 30px !important;}
	.posts  hr:last-of-type, .thread-nav-mob + hr {margin:0.5em auto !important;}
	.post .post-details {overflow: hidden;}
	.hidden-thread-box {margin-left: 0 !important; 	padding: 5px;}
	.board-list-mob {display: block !important;	float:left;clear: none !important; }
	.menu {
		position: fixed;
		width: 100%;
		background: #DDDDDD;
		padding: 3px !important;
		border-bottom: 2px solid #cccccc;
	}
	.menu-links-mob {float: right !important;}
	.thread-nav-mob {display: block !important; text-align: center !important;}
	.send-mob {text-align: center;margin: 3px 0;}
	.button-mob {
		float: none !important;
		border-radius: 4px;
		padding: 6px 10px 5px 10px !important;
		font-weight: bold;
		background-color: #DDDDDD;
		border: 1px solid #CCCCCC;
		user-select: none;
		background-repeat: repeat-x;
		text-decoration: none;
		color: #FF6600;
		display: inline-block !important;
		cursor: pointer;
	}
	.pager {text-align: center;	}
	}
	#plashque {
		z-index: 1000
	}
  /* MAKABA DEFAULT END */
  
  
  html, body {
    font-family: "Trebuchet MS",Trebuchet,tahoma,serif !important;
    font-size: 16px !important;
    background-color: #f4f5f5 !important;
  }
  

  
  img, .img {
    border-radius: 8px;
  }
  
  a {
    color: #444 !important;
    text-decoration:none !important;
  }
  
  a:hover {
    color: #888 !important;
  }
    
  hr {
    border: 0 !important;
  }

  .posts .post-wrapper, .posts .oppost-wrapper, .thread .oppost, .thread .post {
    width: 100% !important;
    clear: both !important;
  }

  .posts, form > .thread {
    width: /*[[size]]*/px !important;
    margin-left: auto !important;
    margin-right: auto !important;
  }
  
  #adminbar, .adminbar-top {
    margin-bottom: 0px !important;
    /* padding-bottom: 10px !important;*/
    padding: 8px !important;
    background: rgba(0, 0, 0, 0.08) !important;

  }
  
  header {
    /*padding: 8px !important;*/
  }

  .oppost-wrapper {
    color: #777 !important;
    background: #fff !important;
    min-height: 30px;
    position: relative !important;
    border: none !important;
    border-left: 5px solid #c1e1fd !important;
    border-radius: 8px !important;
    box-shadow: -1px 1px 1px rgba(0,0,0,.08);
    margin-bottom: 4px;
    overflow: auto;
  }
  
  .oppost-options-mob {
    font-size: 0.8em;
    overflow: auto;
    padding: 8px;
    margin: 0 8px;
    background: none repeat scroll 0% 0% #fff !important;
  }
  
  .oppost {
    background: none repeat scroll 0% 0% #fff !important;
  }
  
  .post {
    padding: 0px;
    margin: 0px;
  }

  .post-wrapper {
    margin: 0;
  }
  
  .post .post-message {
    margin-top: 0 !important;
  }

  .post .image {
    display: inline-block;
    vertical-align: top;
    margin: 8px 17px;
  }

  .post .post-details {
    background: none repeat scroll 0% 0% #fff !important;
    padding: 8px;
    margin: 0 8px;
    font-size: 0.8em !important;
  }
  
  .post .images-single + blockquote {
    margin: 1em;
  }
  
  .reply {
    color: #777 !important;
    background: #fff !important;
    min-height: 30px;
    border: none !important;
    border-radius: 8px !important;
    box-shadow: -1px 1px 1px rgba(0,0,0,.08);
  }


  .ABU-refmap {
    padding: 8px;
    margin: 0 8px 8px 8px;
  }
  
  .reply.hidden-thread-box, .hidden-post-box {
    color: #777 !important;
    background: #fff !important;
    min-height: 30px;
    padding: 12px 12px !important;
    position: relative !important;
    border: none !important;
    border-left: 5px solid rgb(247, 184, 150) !important;
    border-radius: 8px !important;
    box-shadow: -1px 1px 1px rgba(0,0,0,.08);
    margin-bottom: 4px;
    overflow: auto;
  }
  
  .reply.hiclass {
    border-left: 5px solid #EEDACB !important;
    margin: 0em 0em 0em 1em !important;
  }

  .unkfunc {
    color: #2069A8 !important;
  }

  span.spoiler, span.spoiler a {
    color: #f4f5f5;
    background: none repeat scroll 0% 0% #f4f5f5 !important;
  }    

  .new-posts-marker .post {
    border-bottom: 2px solid #F00 !important;
  }

  .ophui {
    color: #008000;
  }

  .button-mob {
    color: #fff !important;
    background-color: #5bc0de !important;
    border-color: #46b8da !important;
    border-radius: 4px !important;
    padding: 8px 16px 8px 16px !important;
    text-decoration: none !important;
  }

  .button-mob:hover {
    color: #fff !important;
    background-color: #31b0d5 !important;
    border-color: #269abc !important;
    text-decoration: none !important;
  }
  
  input[type="button"], input[type="submit"] {
    color: #fff !important;
    background-color: #5cb85c !important;
    border-color: #4cae4c !important;
    border-radius: 4px !important;
    text-decoration: none !important;
  }

  input[type="button"]:hover, input[type="submit"]:hover {
    color: #fff !important;
    background-color: #449d44 !important;
    border-color: #398439 !important;
    text-decoration: none !important;
  }

  .postpanel, .search {
    display: inline-block !important;
  }
  
  .menu {
    z-index: 200;
  }

  .rules-area, .kupi-passcode-suka {
    display: none !important;
  }

  #postform {
    text-align: center !important;
  }

  #postform .mail input[type="text"], #postform td select#anoniconsselectlist, #postform td #shampoo, #postform td input#subject {
    width: 100% !important;
    min-width: 600px !important;
  }
  /*
  .mainlogo {
    margin: 200px !important;
    padding: 100px !important;
  }
  */
  body {
    background: url("https://2ch.hk/newtest/resources/images/descbg.gif") repeat scroll 0% 0% transparent !important;;
  }
  
  .button {
    display: none !important;
  }
  .search {
    margin-right: 5px; 
  } 
 }
