@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pandora.fallengalaxy.com"),
               domain("ragnarok.fallengalaxy.com"),
               domain("caron.fallengalaxy.com") {
  /*!
   *  Font Awesome 4.3.0 by @davegandy - http://fontawesome.io - @fontawesome
   *  License - http://fontawesome.io/license (Font: SIL OFL 1.1, CSS: MIT License)
   */

  @font-face {
    font-family:'FontAwesome';
    src:url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.eot?#iefix&v=4.3.0') format('embedded-opentype'),
      url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.woff2?v=4.3.0') format('woff2'),
      url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.woff?v=4.3.0') format('woff'),
      url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.ttf?v=4.3.0') format('truetype'),
      url('http://maxcdn.bootstrapcdn.com/font-awesome/4.3.0/fonts/fontawesome-webfont.svg?v=4.3.0#fontawesomeregular') format('svg');
    font-weight:normal;
    font-style:normal
  }

  .msg .sender, .msg.whisper {
    color: #aaaaaa !important;
  }

  .msg td.allyTag {
    width: 44px !important;
  }
  .msg td.allyTag:before {
    font-family: FontAwesome;
    padding-right: 6px;
    color: #ffffff;
    text-align: left;
  }
  .channel-faction td.allyTag:before {
    content:"\f007"
  }
  .channel-selfEmbassy td.allyTag:before {
    content:"\f015"
  }
  .channel-embassy td.allyTag:before {
    content:"\f07b"
  }
  .channel-public td.allyTag:before {
    content:"\f0c0"
  }
  .channel-moderator td.allyTag:before {
    content:"\f0c3"
  }
  .channel-ally td.allyTag:before {
    content:"\f0e8"
  }
  .channel-custom td.allyTag:before {
    content:"\f21b"
  }
  .whisper td.allyTag:before {
    content:"\f026"
  }

}
