css:
/* Version: 1.3.1 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(groupees.com) {

  #mce-EMAIL,
  .chat-box .chat,
  .chat-box .chat-form input[type="text"],
  .chat-box .chat-form .emoticons ul {
    background-color: #373737; /* Fall back property in case browser does not support linear-gradient() */
    background: linear-gradient(to bottom, #666, #262626) !important;
    color: lightgray !important;
  }

  .chat-box .chat-form,
  .chat-box .form-placeholder {
    background-color: #262626 !important;
  }
  
  .chat-box .chat .log .message {
    border-bottom: 1px solid transparent;
  }

  .chat-box .user-popup {
    background-color: #2B2B2B !important;
    color: lightgray !important;
    border: 2px solid steelblue;
  }

  .chat-box .chat-form .emoticons ul li {
    border-bottom: 1px dotted steelblue;
  }

  .chat-box .chat-form .emoticons ul li:hover {
    color: #34B0EF !important;
  }

  .chat-box .tab.active {
    background: #81BB21;
    background: linear-gradient(to top, #94D05A, #3E6E1D) !important;
  }

  .chat-box.compact span.tab a.caption:after { /* Affect Chrome users only since default has -webkit-linear-gradient() which made chat tabs look faded on the right. This declaration removes the effect. */
    background: none !important;
  }
}
