css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {

    html {
    	color: #E5E5E5 !important;
    	background: none repeat scroll 0 0 #222222 !important;
    }   
    body, .zci, .result__snippet {
        
    }
    
    .result__snippet
    {
        color: #545454;
        font-size: 13px;
        background: transparent;
        font-weight: 400;
        line-height: 18.2;
        background: #FFF !important;
        height: 75px;
        line-height: 1.3;
        margin-left: -8px;
        
        padding: 10px;
    }
    .result, .results, .results_links, .highlight_v, .result__snippet {
        background-color: #FFF !important;
        width: 100%;
        font-family: arial, sans-serif;
    }
    
    .result__snippet b
    {
        color: black !important;
    }
    .results highlight {
        background-color: #FFF !important;  
    }
    .header, header cw {
    }
    .header__search-wrap {
        margin-right: 19px;
        background:#F1F1F1;
        padding:9px;
        padding-top: 2px;
        padding-bottom: 4px;
        border-bottom: 1px solid #E5E5E5;
        
      
        
    }
    .header__search
    {
        margin-left: 118px;
        margin-top: 11px;
        height: 40px;
        background: #F1F1F1;
    }
    .btn--primary, .btn {
        background: #FFF;
        border: 1px solid #333;
        color: #888;
    }
    .cloudsave {
        border-radius: 2px;
        padding: 1em;
        background: none repeat scroll 0% 0% #282828;
        text-align: center;
        margin: 0px 0px 1em;
    }
    .set-reset, .set-bookmarklet {
        border-radius: 3px;
        margin: 1em 0px;
        background: #282828;
        border: 1px solid #333;
        text-align: center;
    }
    .result__body .links_main
    {
        padding-top: 10px;
    }
    .set-main .frm__label {
        font-size: 1.3em;
        padding: 0px;
        font-weight: 400;
        color: #ccc;
    }
    .header__logo-wrap
    {
        margin-top:8px;
    }
    .site-wrapper-border
    {
        display:none;
    }
    .frm__switch__label {
        background: #333;
        border: 1px solid #333;
        color: #888;
    }
    .frm__select select {
        background: #333;
        border: 1px solid #333;
        color: #888;
    }
    .frm__select {
        border-radius: 3px;
        border: 1px solid #383838;
        background-color: #222;
    }
    .frm__select:after {
        background-color: #333;
        border-color: #888 transparent transparent;
    }
    .search__button, .search__button--hero {
        margin-top: 0px;
        width: 60px;
        padding: 0px;
        z-index:8;
        border-radius: 0px;
        right: -2px;
        min-height: 31px;
        color: white !important;
        font-weight: bold;
        
        margin-top: -1px;
        
    }
    
    .cw--c
    {
        margin: auto !important;
    }
    
    
    #search_button {
        
        background: #4D90FE;
        background-image: url-moz-linear-gradient(50% 0%, #4D90FE, #4787ED);
        
        
    }
    input#search_button:hover
    {
        background:#357AE8;
        background-image: -moz-linear-gradient(center top, #4D90FE, #357AE8);
        border: 1px solid #2F5BB7;
    }
    
    #search_form:hover #search_button
    {
        background: #4D90FE none repeat scroll 0% 0%;
        color: white;
    }
    
    #search_form_input_clear {
        display: none;
        padding-top: 2px;
        padding-bottom: 1px;
        padding-top: 3px;
        padding-top: 3px;
        margin-right: 40px;
        margin-bottom: 3px;
    }

    .header__button--menu, .header__button--menu highlight{
        background-color: #333 !important;
        border: 1px solid #222;
        margin: 15px;
    }
    
    .header-wrap--home
    {
        border-top: 0;
    }
    #header_wrapper
    {
        border: none !important;
    }
    
    
    
    .region-indicator {
        background-color: #333333;
        margin: 15px;
        padding: 5px;
    }
    .popover__box {
        background-color: #333333;
    }
    .region__body {
       background-color: #333333;
    }
    .popover--dk:target, .popover--dk.is-showing {
       background: none repeat scroll 0% 0% rgba(21, 21, 21, 0.7);
       z-index: 100;
    }
    .nav-menu--slideout {
       background: none repeat scroll 0% 0% #181818;
       border-left: 0px solid #222;
    }
    .cw, .cw--c {
       position: relative;
       padding: 16px 2.5em;
       max-width: 70em;
       margin-left: 0px;
       margin-right: auto;
    }
    .has-zcm .header-wrap:before, .has-zcm .header-wrap:after, .has-zcm .header:after {
       box-shadow: none;
       background: none;
    }
    .badge--official {
        background-color: #447799;
    }
    
    
    .header-wrap, #header_wrapper {
        background-color: #FFF;
        height: 8em;
    }
    .header__button--menu {
        border: 1px solid #222;
        margin-top: 17px;
    }
    
    
    .search--header {
       background-color: #FFF;
       height: 32px;
       border-radius: 0px;
       font-family: arial !important;
       border: 1px solid #ccc;
    }
    .result, .cw .results--didya, .cw--c .results--didya {
        background-color: #222222;
    }
    .site-wrapper:before {
        background-color: #4477aa;
    }
    a {
        color: #76C7FF !important;
    }
    #header_wrapper
    {
        padding-top: 0px;
    }
    #header_wrapper #header {
        
        padding-top:0px;
        
    }
    
    #header
    {
        width: 102%;
            width: 100% !important;
       max-width: 100% !important;
        margin-right: 0px;
    }
    
    #search_form, #search_form_homepage {
        font-family: arial, sans-serif !important;
        padding-right: 0px;
        
    }
    
    #search_form_homepage
    {
        border-color: #ccc !important;
        height: 25px;
        border-radius: 0px;
        background: white;
        border: 1px solid #CCC !important;
    }
    
     #search_form_homepage:active, #search_form_homepage:hover, #search_form_homepage:focus
    {
        border: 1px solid #4D90FE !important;

    }
    
    
    #search_form_input, #search_form_input_homepage {
        border-color: #888888 !important;
        padding: 0px;
        margin: 0px;
        font-family: arial, sans-serif !important;
        height: 100%;
    }
    #header_button {
        border: 1px solid #444444 !important;
    }
    
    .header__search
    {
        max-width: 44em !important;
    }
    
    #duckbar
    {
        background: white;
        margin-top: -10px;
        margin-bottom: 0px;
        margin-left: 0px;
        padding-left: 120px;
        padding-top: 12px;
        padding-bottom: 11px;
        border-bottom: 1px solid #E5E5E5;
        width: 100%;
    }
    #logo {
        display: none !important;
    }
    #logo_homepage {
        height: 161px !important;
		margin-bottom: 40px !important;
		padding-bottom: 1px !important;
		width: 300px !important;
		}
    
    .has-active-zci.header-wrap, .has-active-zci
    {
        background-color: #F1F1F1 !important;
    }

    #side {
        background-color: #222222 !important;
    }
    #side_suggestions, #side_suggestions a {
        color: #A1A1A1 !important;
    }
    #side_wrapper2 {
        color: #DDDDDD !important;
    }
    #zero_click {
        background: none repeat scroll 0 0 #000000 !important;
    }
    #zero_click a {
        color: #76C7FF !important;
    }
    #zero_click a:visited {
        color: #76C7FF !important;
    }
    #zero_click .highlight {
        background: none repeat scroll 0 0 #F3F3F3 !important;
	      border-color: #444444 !important;
    }
    #zero_click_header a {
        color: #444444 !important;
    }
    #zero_click_header a:visited {
        color: #444444 !important;
    }
    #zero_click_abstract .highlight {
        background: none repeat scroll 0 0 #222222 !important;
	      border-color: #444444 !important;
    }
    #zero_click_topics .highlight {
        background: none repeat scroll 0 0 #222222 !important;
	      border-color: #444444 !important;
    }
    #zero_click_wrapper {
        display: none !important;
        visibility: hidden !important;
    }
    .web-result:hover, .zero-click-result:hover {
        background: none repeat scroll 0 0 #000000 !important;
        border: 1px solid #BFBFBF !important;
        border-radius: 0px !important;
        cursor: default !important;
    }
    #links .icon_fav img, .icon_fav2 img {
        height: 24px !important;
        width: 24px !important;
    }
    #links .results_links_deep {
        border: 0px none !important;
    }
    .results_links, .results_zero_click, .results_disambig, .results_links_more, .results_category_more, .results_disambig_more {
        border: 0px none !important;
    }
    .navbutton {
        color: #76C7FF !important;
    }
    input.navbutton:hover {
        border-bottom: 0px none !important;
    }
    a.large {
        color: #76C7FF !important;
    }
    a.large:visited {
        color: #D7B7FF !important;
    }
    .url, .url a {
        color: #63D684 !important;
    }
    .url a:visited {
        color: #D7B7FF !important;
    }
    .links_menu, .links_menu a {
        color: #FFC334 !important;
    }
    .links_menu a:visited {
        color: #D7B7FF !important;
    }
    .snippet, .snippet a {
        color: #E5E5E5 !important;
    }
    #tagline_homepage {
        font-size: 15px !important;
        color: #E5E5E5 !important;
    }
    #tagline_homepage a {
        color: #76C7FF !important;
    }
    #tagline_homepage a:visited {
        color: #76C7FF !important;
    }
    #dd-titleblock .dd-title {
        color: #E5E5E5 !important;
    }
    #introblock h3 {
        color: #E5E5E5 !important;
    }
    #introblock p {
        color: #E5E5E5 !important;
    }
    #footer_homepage_left {
        color: #B0B0B0 !important;
    }
    #footer_homepage_left a {
        border-right: 1px dotted #E5E5E5 !important;
        color: #B0B0B0 !important;
    }
    #footer_homepage_right a {
        border-left: 1px dotted #B0B0B0 !important;
        color: #B0B0B0 !important;
    }
    .modal .icon, .add_to_browser .icon, #footer_homepage .add_to_browser .icon, #footer_homepage .modal .icon {
        color: #666666 !important;
    }
    #more_menu li a {
        color: #1168CC !important;
    }
    
    .zcm__sep--h
    {
        content: "";
        border: none;
        display: none;
    }
    
    .has-active-zci .header-wrap, .has-active-zci {
        background-color: #333333;
        border: none;
    }
    
    a.zcm__link
    {
        padding-bottom: 13px;
        font-family: arial, sans-serif !important;
        font-size: 13px;
        font-weight: normal;
        padding-left: 8px;
        padding-right: 8px;
        margin-left: 8px;
        margin-right: 8px;
        color: #333 !important;
        
    }
    
    a.is-active
    {
        color: #DD4B39 !important;
        border-bottom: 3px solid #DD4B39;
        padding-bottom: 10px;
        font-weight: bold;
    }
    
    li.zcm__item a.zcm__link::after, a.zcm__link::before
    {
        border-left: 0;
        border-right: 0;
        font-family: arial !important;
        
        content: "";
    }
    
    a.zcm__link.zcm__link--meanings
    {
        content: "Web";
    }
    
    #did_you_means, #did_you_mean
    {
        background: transparent !important;
        font-size: 18px;
        font-weight: 200;
        font-family: arial, sans-serif !important;
        
        
    }
    
    #did_you_means
    {
        border-bottom: 1px solid #EDEDED;
        padding-bottom: 15px;
    }
    
    #did_you_mean b
    {
        color: #DD4B39 !important;
    }
    
    #did_you_mean > b
    {
        font-weight: normal;
    }
    
    #did_you_mean > b::after
    {
        content: ": ";
        margin-left: -4px;
    }
    
    #did_you_mean a, #did_you_mean a b
    {
        color: #1A0DAB !important;   
    }
    
    .highlight_d, .results_links_deep
    {
        background: transparent !important;
        
    }
    
    #links
    {
        float: left;
        margin-top: 0px !important;
    }
    
    .results_links_deep:first-child
    {
        padding-top: 0px;
        margin-top: -56px;
    }
    
    
    
    .content-wrap, .content-wrap .cw, .content-wrap .cw #links_wrapper, #links_wrapper #links, .result__body.links_main, #links, #links_wrapper
    {
        background: white !important;
  
    }
    
    #zero_click_wrapper
    {
        display: block !important;
        padding-top: 0px;
        
        float:left;
        width: 100%;
        padding-top: 30px;
        margin-top: -37px;
        
        
    }
    
    .tag-home
    {
        display: none;
    }
    
    #search_button_homepage
    {
        float: right;
        margin-top: 30px;
        margin-right: 40%;
        width: 100px;
        content: "Ducky search";
        background: white none repeat scroll 0% 0%;
        border: 1.5px solid rgb(153, 153, 153);
        color: rgb(153, 153, 153) !important;
        font-weight: bold !important;
    }

    
    #links_wrapper
    {
        padding-right: 100px;
        width: 76%;
        padding-top: 15px;
    }
    
    h2.result__title, h2.result__title a
    {
        width: 100%;
        color: #1A0DAB !important;
        font-size: 18px;
        line-height: 21.6px;
        font-weight: 400;
        text-decoration-style: solid;
        font-family: arial, sans-serif !important;
        margin-top: 30px;
        padding-bottom: 0px;
    }
    
   
    
    .header--aside
    {
        display: none;
    }
    a.result__check
    {
        padding-top: 35px;
        display: none;
    }
    
    .result__extras
    {
        margin-top: -105px;
        font-size: 14px;
        line-height: 16px;
        
    }
    
    .result__extras a
    {
        color: #006121 !important;
    }
    
    .result__snippet
    {
        margin-top: 10px;
    }
    
    h2.result__title > a.result__a > b
    {
        color: #1A0DAB !important;
    }
}
