css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("houzz.com") {

  .hbs, body.hbs {
    font-family: "proxima-nova","Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 14px;
    line-height: 1.42857;
    color: #394A62;
    background-color: #121010;
}
  a{    color: #0D4DA1 !IMPORTANT;
}
  .whiteCard.xl .photoMeta .uploader, .whiteCard.xl .photoMeta .photoInfo {
    padding: 10px;
    overflow: hidden;
    color: #394A62;
    background-color: #121010;
}
  .whiteCard{
        color: #394A62;
    background-color: #121010;

  }
  .whiteCard.xl .photoMeta .stats {
    background: #1A1717 none repeat scroll 0% 0%;
    font-size: 13px;
    color: #888;
    padding: 7px 10px;
}
  
  .photoBannerBottom .pro-see-projects {
    background: #121010 none repeat scroll 0% 0%;
    overflow: hidden;
    vertical-align: middle;
    height: 100px;
    text-align: center;
    line-height: 100px;
}
  
  #lbBg {
    top: 0px;
    left: 0px;
    bottom: 0px;
    right: 0px;
    background-color: #121010;
}
  
  #lbInfo {
    background-color: #121010;
    line-height: 1.5;
    font-size: 13px;
    width: 340px;
}
  
  #lbNotification {
    position: absolute;
    top: -40px;
    right: 0px;
    z-index: 10;
    width: 340px;
    height: 40px;
    background-color: #121010;
    color: #333;
    border-bottom: 1px solid #F0F0F0;
}
  
  #lbQuestions .feedItem {
    border: 1px solid #DEDDDB;
    padding: 8px 8px 0px;
    overflow: hidden;
    background-color: #17181B;
    margin-bottom: 20px;
}
  #lbRoot *, #lbRoot *::before, #lbRoot *::after {
    box-sizing: border-box;
    background-color: #13151B;
}
 .lbMouseMoving #lbImage:hover .fadeOutOnIdle,.lbMouseOver #lbImage .fadeOutOnExit{opacity: 0.4;filter: alpha(opacity=40)}
  
  
  .spaceContent {
    background-color: #101014;
}
  
  .feedItem {
    border: 1px solid #E6E6E6;
    margin-bottom: 25px;
    padding: 0px;
    overflow: hidden;
    background-color: #100F17;
}
  
  #questionsContainer .commentsContainer .comment {
    background-color: #16141D;
}
  
  .feedComments .moreComments, .feedComments .addComment {
    background-color: #150F0F;
}
  
  .hbs .form-control, .hbs input[type="text"], .hbs input[type="password"] {
    display: block;
    width: 100%;
    height: 30px;
    padding: 4px 6px;
    font-size: 14px;
    line-height: 1.42857;
    color: #555;
    background-color: #353B42;
    background-image: none;
    border: 1px solid #CCC;
    border-radius: 3px;
    transition: border-color 0.15s ease-in-out 0s, box-shadow 0.15s ease-in-out 0s;
}
  
  #spaceBuzzes {
    width: 638px;
    margin-top: 40px;
    line-height: 1.5;
    background-color: #13151B;
}
  
  div.rGDiv {
    width: auto;
    border: 1px solid #DEDDDB;
    padding: 8px;
    overflow: hidden;
    background-color: #13151B;
    margin-bottom: 20px;
}
  
  #footerContainer {
    border-top: 1px solid #CCC;
    background-color: #121010;
}
  .houzz-header {
    background: #2D2C30 none repeat scroll 0% 0%;
    height: 60px;
    border-bottom: 1px solid #E6E6E6;
    position: relative;
    z-index: 2;
}
  .houzz-header-secondary {
    background: #050508 none repeat scroll 0% 0%;
    height: 43px;
    border-top: medium none;
    border-bottom: 1px solid #E6E6E6;
    width: 100%;
    position: relative;
    z-index: 1;
    left: 0px;
    right: 0px;
    top: 0px;
}
  #mainArea {
    min-width: 1000px;
    background-color: #121010;
}
  .ask-form {
    padding: 10px;
    background-color: #1D1A1A;
    margin-top: 0px;
    margin-bottom: 25px;
}
  
  .new-homepage .block {
    margin-bottom: 25px;
    padding: 15px;
    border: 1px solid #E6E6E6;
    background: #1A181E none repeat scroll 0% 0%;
}
  
  .feedContainer .attachment.gallery .feed-gallery-summary {
    padding: 20px 35px;
    position: relative;
    background-color: #121115;
}
   
}
