css:
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {

    /* Main Body */
    body { 
        background: #e0e0e0 !important;
        font-family: Calibri, sans serif !important;
        line-height: 1.4em !important;
        text-align: justify !important;
        color: #808080 !important;
    }
    .identity-refresh #hello_glass.blue, .plexi.show.color, .identity-refresh .ui_peepr_glass, .post-forms-glass, .glass.blue, #hello_glass.blue, #glass_overlay.blue { background: rgba(0,0,0,0.3) !important; }
    .ui_dialog_lock { background: rgba(0,0,0,0.5) !important; }
    .dashboard_iframe { background: none !important; }
    #tinymce { background: #ffffff !important; }
    .search_results_flex .l-container:before { background: #f0f0f0 !important; }
    .l-content { background: #f0f0f0 !important; border-radius: 4px !important; }
    .left_column { margin-top: 8px !important; margin-bottom: 18px !important; }
    .l-container.l-container--two-column-dashboard .left_column { padding-top: 10px !important; padding-left: 10px !important; }
    .l-container.l-container--two-column .right_column, .l-container.l-container--two-column-dashboard .right_column { padding-top: 30px !important; padding-right: 5px !important; }
    #left_column, #right_column{ background: transparent !important; }
    .elevator::before { color: #c0c0c0 !important; font-size: 1.5em !important; }
    .elevator-wrapper { right: 25px !important; }
    
    /* Header */
    .l-header-container--refresh { background: #cacaca !important; }
    .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container { border: none !important; }
    .tab_bar { margin-right: 15px !important; }
    .tab_notice { 
        background: #D95E40 !important; 
        left: 38px !important; 
        top: -5px !important; 
        height: 19px !important; 
        margin-top: 0 !important;
        text-align: center !important; 
    }
    .tab_notice .tab_notice_value { color: #e0e0e0 !important; }
    .tab-notice--outlined { border: none !important; }
    .blog-list-item .blog-list-item-unread-notice { border: none !important; transform: scale(0.8) !important; margin: -4px 0 0 -10px!important; background-color: #D95E40 !important; line-height: 1.55em !important; }
    
    /* Post icons */
    .post-forms-above-glass.post_container { margin-top: 0 !important; }
    
    /* Notifications */
    .notification .notification_sentence a { color: #808080 !important; }
    .logo .svg-logo { margin-left: 10px !important; }
    .notification .notification_sentence .username, .notification .notification_sentence .banner_text { color: #a0a0a0 !important; }

    /* Posts */
    .post_full .post_permalink { 
        border-color: transparent !important; 
        background: transparent url("http://fc00.deviantart.net/fs70/f/2015/051/e/d/cross_16x16_by_mediocrememory-d8irjf3.png") center no-repeat !important; 
        box-shadow: none !important; 
    }
    .post_full .post_permalink { transition: all ease-in .3s !important; }
    .post_full:hover .post_permalink { opacity: .8 !important; }
    .post_header { font-size: 1em !important; }
    .post_avatar {background: #f0f0f0 !important;}
    .post-typography-update .post.is_conversation .post_content .post_title, .post-typography-update .post.is_regular .post_content .post_title, .post-typography-update .post .post-content .title-field * { font-family: Cambria, Georgia, Times New Roman, Serif !important; }
    .post-typography-update .post .post-content a, .post-typography-update .post .post_body a { color: #808080 !important; }
    
    /* Post Editor */
    .post-typography-update .post.is_conversation .post_content .post_title, .post-typography-update .post.is_regular .post_content .post_title, .post-typography-update .post .post-content .title-field *  { color: #a0a0a0 !important; }
    .tag-input-wrapper .editor-plaintext, .tag-input-wrapper .editor-placeholder, .tag-input-wrapper .ghost { font-size: .85em !important; }
    .editor-richtext, .editor-plaintext { color: #808080 !important; }
    .post-form--tag-editor .tag-label { font-size: 1em !important; }
    
    /* Search bar */
    .ui_search { color: #fff !important; }
    .ui_search .search_query { background: #cacaca !important; border: none !important; color: #fff !important; }
    .ui_search .search_form_field:before { top: 15px !important; color: #fff !important; }
    .ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before { border-color: transparent !important; color: #909090 !important; line-height: 1.05em !important; }
    
    /* Search */
    .search_results_flex #container:before { background: #282828 url('https://31.media.tumblr.com/d2794cad167eaf71f5e2695cf3d07cdb/tumblr_inline_n1r5c46tLf1qmpver.jpg') top center repeat-x fixed !important; }
    .search_results_container .posts_wrapper, .search_results #left_column, #right_column {background-color: transparent !important;}
    .search_form_active .search_form_field:after, .search_form_active .search_form_field:before { border: none !important; }
    .search_results_container .tumblelog_mask_item { background-color: #282828 !important; }
    
     /* Fanmail */
    .post.fan_mail .message { 
        background: #ffffff !important;
        font-size: 1.1em !important;
        font-family: Times New Roman, serif !important;
        line-height: 1.3em !important;
    }
    .post_avatar.no_nipple:after { display: inline !important; }
    .post.fan_mail .before { background: #ffffff !important; border-radius: 6px 6px 0 0 !important; }
    .delete_all_messages { font-weight: bold !important; }
    .delete_all_messages:hover { text-transform: none !important; background-color:  #282828 !important; }
    .identity .send_fan_mail .hide_overflow { color: #fff !important; }
    
    /* Right Column */
    .section_header { font-family: Cambria, Georgia, Times New Roman, Serif !important; font-weight: normal !important; font-size: 1em !important; color: #D95E40 !important; }
    .recommended-blogs-unit-header { color: #56BC8A !important; }
    .radar_header { color: #529ECC !important; }
    .radar .radar_avatar::before { background: #f0f0f0 !important; }
    .follow_list_item_blog::before { background: none !important; border: none !important; }
    .identity .controls_section .open_blog .currently_selected_blog { 
        color: #F2992E !important; 
        font-size: 1.35em !important; 
        text-transform: uppercase !important; 
        max-width: 200px !important; 
        font-family: Cambria, Georgia, Times New Roman, Serif !important; 
    }
    .tag_controls .tag { color: #808080 !important; font-weight: normal !important; font-family: Cambria, Georgia, Times New Roman, Serif !important; font-size: 1.5em !important; max-width: 225px !important;}
    .explicit_content_lock .lock_icon { background-color: #e0e0e0 !important; padding: 3px !important; opacity: 1 !important; border-radius: 3px !important; background-position: -126px 2px !important; }
    .small_text { color: #b0b0b0 !important; }
    .right_column a { font-size: 1.1em !important; color: #808080 !important; font-weight: normal !important; }
    .right_column:after { visibility: hidden !important; }
    .name, .subheading, .count, .tumblelog_title, .tumblelog_name { color: #808080 !important; font-weight: normal !important; max-width: 100px !important; }
    .controls_section a canvas { opacity: 1 !important; background-color: #e0e0e0 !important; padding: 5px !important; border-radius: 3px !important; margin: 5px -3px 0 0 !important; }
    .controls_section_item, li.item, .identity .controls_section li { transition: background-color ease .5s !important; -webkit-transition: background-color ease .5s !important; }
    .controls_section_item:hover, li.item:hover, .identity .controls_section li:hover { background-color: #e5e5e5 !important; border-color: transparent !important; }
    .rapid-recs-container .rapid-recs { visibility: hidden !important; }
    .controls_section, .open_blog.with_subtitle { border-bottom: 2px solid #e5e5e5 !important; }
    .follow_icon { color: #fff !important; font-size: 1.6em !important; margin: 1px 0 0 0 !important; transition: all ease .5s !important; -webkit-transition: all ease .5s !important; }
    
    /* Post Footer and Notes */
    .note_link_current, .post-source-footer a, .post_info_link { transition: all ease .5s !important; -webkit-transition: all ease .5s !important; }
    .note_link_current:hover, .post-source-footer a:hover, .post_info_link:hover { color: #529ECC !important; }
    .note:first-child { border-top: none !important; }
    .note { text-align: left !important; border-color: #e5e5e5 !important; transition: all ease .5s !important; -webkit-transition: all ease .5s !important; }
    .note:hover { background-color: #f5f5f5 !important; }
    .follow { background: #c0c0c0 !important; border-color: #c0c0c0 !important; transition: all ease .5s !important; -webkit-transition: all ease .5s !important; }
    .follow:hover { background: #F2992E !important; border-color: #F2992E !important; }
    .post_notes .notes_outer_container.popover .note.is_following, .post_notes .notes_outer_container.popover .note.is_me { background-color: #f5f5f5 !important; }
    li.notification.single_notification.alt.takeover-container.big-margin { visibility: hidden !important; height: 0px !important; margin: 0 !important; padding: 0 !important; }
    
    /* Navigation */
    #dashboard_controls a, #dashboard_controls .control { color: #808080 !important; text-shadow: none !important;}
    .dashboard_controls_arrow_first_page, .dashboard_controls_arrow_left,  .dashboard_controls_arrow_right { filter: grayscale(100%) !important; -webkit-filter: grayscale(100%) !important; -webkit-filter: grayscale(1) !important; }
    .search_popover .popover_menu_item:hover, .popover_menu_item:hover, .search_popover .result_link:hover, .blog-list-item.blog-list-item--on-light:hover { background-color: #f8f8f8 !important; }
    .popover.search_popover .search_subheading, .popover_header { margin: 0 !important; background-color: #f0f0f0 !important;}
    .blog-sub-nav-details .blog-sub-nav-item:hover, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item.currentItem { box-shadow: -4px 0 0 #fff, 10px 0 0 #fff !important; background-color: #fff !important; }
    
    /* Buttons */
    .flat-button { background: #b5b5b5 !important; border: none !important; border-radius: 2px !important; }
    .chrome.blue, .editor_navigation .header_button button, .flat-button.blue { background: #A77DC2 !important; border: none !important; border-radius: 2px !important; }
    .send_fan_mail { background: #56BC8A !important; }
    .compose-button { margin-right: 15px !important; margin-left: 10px !important; background: #cacaca !important; fill: #fff !important; transition: fill ease .3s !important;}
    .compose-button:hover { fill: #529ECC !important;  }
    .chrome { transition: all ease 1s !important; -webkit-transition: all ease 1s !important; border: none !important; }

    /* Queue */
    .dashboard_options_form { border: 1px solid #232323 !important; color: #808080 !important; }
    .dashboard_options_form .dashboard_options_form_disclaimer, .dashboard_options_form a { color: #808080 !important; }
    .publish_info { color: #808080 !important; }

    /* Avatars */
    .post_avatar .post_avatar_link { background-color: #f0f0f0 !important; }
    
    /* Activity */
    .notifications_page .section.divider { background: none !important; }
    
    /* Mass Editor */
    #nav_archive.blue_bar { background: #232323 !important; box-shadow: none !important; }
    .old_archive { background: none !important; }
    .old_archive #left_column, #right_column {background-color: transparent !important;}
    .editor_navigation .header_button button { color: #808080 !important; transition: all ease .3s !important; }
    .editor_navigation .header_button button:hover { background: #D95E40 !important; border-color: #D95E40 !important; }
    #browse_months { border-left: 1px solid #ffffff !important; }
    .brick.highlighted .highlight { background: rgba(256,0,0,0.5) !important; border: 3px solid rgba(256,0,0,0.8) !important; border-radius: 3px !important; }
    .tag_count { background: rgba(256,0,0,0.8) !important; }

    /* Settings */
    .settings_group_wrapper { background: #f0f0f0 !important; padding-bottom: 20px !important; border-radius: 0 0 5px 5px !important; margin-bottom: -15px !important; }
    .apps_heading { background: none !important; font-size: 1.2em !important; }
    
    /* XKit */
    #xinbox_search_button, #xinbox_mass_edit_button { padding-left: 40px !important; line-height: 2.2em !important; margin-left: -10px !important;}
    #x1cpostage_box { background-color: #f0f0f0 !important; border: 1px solid #c9c9c9 !important; }
    #x1cpostage_reblog:hover, #x1cpostage_queue:hover, #x1cpostage_draft:hover {background: #e0e0e0 !important;}
    #xkit-quick-tags-window, .controls_section a .count { filter: grayscale(100%) !important; -webkit-filter: grayscale(100%) !important; -webkit-filter: grayscale(1) !important; }
    .small_links.by-xkit, .small_links { font-size: .8em !important; }
    .xkit-reply-button { color: #808080 !important; padding: 2px !important; border-radius: 1px !important; }
    #xkit-post-separator {border-color: #e0e0e0 !important; }
    
    /* Toast Hell */
    #toaster { visibility: hidden !important; }
    
    /* Global Footers */
    .footer_links a { color: #a5a5a5 !important; }
    
     /* Extras */
    #site_notice { background: #D95E40 !important; border-bottom: 1px solid #181818 !important;  }
    .audio_tabs li { border: none !important; }
    .help_search { margin-left: -15px !important; }
    #following.clearfix {background: #f0f0f0 !important; padding: 10px !important; border-radius: 6px !important; }
    img.content_top, img.content_bottom { visibility: hidden !important;}
    #finder_container{background: #f0f0f0 !important;}
    .yam-plus-ad-container { visibility: hidden !important; height: 0 !important; margin: 0 !important; padding: 0 !important; }
    .notifications_page .section.first { margin-top: 10px !important; border-radius: 4px !important; border: none !important; }
    .ui_notes .ui_note { border-color: #e5e5e5 !important; }
    .ui_notes .ui_note.is_friend { background-color: #f0f0f0 !important; }
    
}
