css:
@-moz-document domain("vk.com") {
	
	
#myprofile .left_label,
#l_fr .left_label,
#l_ph .left_label,
#l_vid .left_label,
#side_bar a[href*="audio"] .left_label,
#l_msg .left_label,
#l_nts .left_label,
#l_gr .left_label,
#l_ev .left_label,
#l_nwsf .left_label,
#l_nws .left_label,
#side_bar a[href*="fave"] .left_label,
#l_set .left_label,
#l_ap .left_label,
#side_bar a[href*="docs"] .left_label,
#l_ads .left_label,
#l_spr .left_label {
	overflow: hidden;
	width: 0px;
}
	#side_bar .left_icon {
    background: none;
		height: 0;
}
	#side_bar ol {
    list-style: none;
    margin: 0 0 10px;
    padding: 0;
}
	
#side_bar .left_label:before {
	position: absolute;
}

#l_fr .left_label:before {
	content: "friend";
}
#l_ph .left_label:before {
	content:"img";
}
#l_vid .left_label:before {
	content:"video";
}
#side_bar a[href*="audio"] .left_label:before {
	content:"mp3";
}
#l_msg .left_label:before {
	content:"sms";
}
#l_nts .left_label:before {
	content:"заметки";
}
#l_gr .left_label:before {
	content:"group";
}
#l_nwsf .left_label:before {
	content:"news";
}
#l_nws .left_label:before {
	content:"comments";
}
#side_bar a[href*="fave"] .left_label:before {
	content:"fav";
}
#l_set .left_label:before {
	content:"options";
}
#l_ap .left_label:before {
	content:"⚠";
}
#side_bar a[href*="docs"] .left_label:before {
	content:"doc";
}
}
