css:
@-moz-document domain("kledgeb.blogspot.jp"), domain("kledgeb.blogspot.com") {
/*======================================
  ubuntu kledgeb ja jp liquid compact
  Last Update: 2018-09-25
  ======================================*/
/*--- unwanteds ---*/
#section_Header #HTML7,
#section_RightBar1 .cls_RightBarAdsence_divAdsence,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
body {min-width: auto !important; min-height: auto !important;}

/*--- header ---*/
#section_Header {padding: 0;}
.cls_HeadBar {padding: 0 !important;}
.cls_TopBar_anchorTitle {display: inline;}


/*--- footer ---*/


/*--- main contents ---*/
.cls_ContentsAndRightBar {width: 100% !important; border-top: 1px solid #ddd;}
.cls_Contents {width: calc(100% - 300px); margin: 0; padding: 0; float: left;}
.cls_Contents>* {width: auto; margin: 0;}
.cls_RightBar {width: 300px; margin: 0; float: right;}
.cls_RightBar * {max-width: 300px;}

#section_RightBar1, #section_RightBar3, #section_RightBar4 {margin-bottom: 0;}

}
