css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://droptun.es/app"){
@font-face{font-family:'Open Sans';font-style:normal;font-weight:400;src:local('Open Sans'),local('OpenSans'),url(https://themes.googleusercontent.com/static/fonts/opensans/v6/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');}
@font-face{font-family:'Open Sans Condensed';font-style:normal;font-weight:700;src:local('Open Sans Cond Light'), local('OpenSans-CondensedLight'),url(http://themes.googleusercontent.com/static/fonts/opensanscondensed/v6/gk5FxslNkTTHtojXrkp-xONSK5BxN3NFS4EJkViHIqo.woff) format('woff');}
body{font-family:'Open Sans','PT Sans','Ubuntu','Hiragino Sans GB',sans-serif!important;}   
::selection{background:#B9B9B9!important;color:#1D1D1D;text-shadow:none!important;}
::-moz-selection{background:#B9B9B9!important;color:#1D1D1D;text-shadow:none!important;}
.header{height:0!important;color:#f3faff;text-shadow:none!important;}
.header a{color:#b4b4b4}
.jp-interface{height:58px!important;}
#lvl-1{position:absolute!important;height:60px!important;}
.nav-help,.nav-app,.social{display:none!important;}
#nav{position:absolute!important;top:10px;z-index:10}
.col-2{width:50px!important;}
.col-3{vertical-align:top!important;padding:8px;} 
.jp-audio{background-image:none!important;background:#3C3C3C!important;border-bottom:1px solid #1C1C1C!important;border-top:1px solid #1C1C1C!important;}
.tunes{background:#1C1C1C!important;padding-top:60px!important;}
.tunes tr:hover{background:#1D1D1D!important;} 
.tunes td{background:none!important;}
tr.tune,tr.alt{color:#b4b4b4!important;}
tr.tune{background-color:#2c2c2c;}
tr.tune.alt{background-color:#212121!important;} 
.jp-playlist{font-family:"Open Sans Condensed","PT Sans","Ubuntu","Hiragino Sans GB",sans-serif!important;font-size:18px!important;height:22px!important;padding:0 0 8px 0!important;}
.tune,.thedir,.jp-current-time,.jp-duration{font-family:'Open Sans','PT Sans','Ubuntu','Hiragino Sans GB', sans-serif!important;font-size:14px!important;}
.jp-playlist,.jp-current-time,.jp-duration{color:#f3faff!important;text-shadow:0 1px 0 #222!important;} 
.file-count{font-style:italic;text-align:right!important;}
.jp-progress{border:none!important;}    
.jp-play-bar{background:#8E8E8E!important;border:none!important;height:8px;top:0px!important;;}
.jp-seek-bar{background:#DDDDDD;border:none!important;top:0!important;left:0!important;}
.jp-current-time,.jp-duration{bottom:5px!important}    
.dirs{background:#272727!important;padding:63px 0 20px!important;border-right:1px solid #1C1C1C!important;}
.dirs-list{width:300px}
.search{background:#B9B9B9;-webkit-box-shadow:inset 0px 0px 20px 0px rgba(0,0,0,0.75);-moz-box-shadow:inset 0px 0px 20px 0px rgba(0,0,0,0.75);box-shadow:inset 0px 0px 20px 0px rgba(0,0,0,0.75);color:rgb(220, 220, 220)!important;border:medium none;border-radius:5px;box-sizing:border-box;color:#7c6a59;margin:6px 8px!important;width:280px;padding:1px;}  
.search input{width:210px;background:none;background:url(http://dl.dropboxusercontent.com/u/56890834/.css/search.png) 236px 7px no-repeat;border:0!important;}
.browser .child a{text-shadow:0 1px 0 #1C1C1C;}
.browser li a{color:#b4b4b4!important}
.browser li{background:url(http://dl.dropboxusercontent.com/u/56890834/.css/folder.png)10px 5px no-repeat;padding-left:20px;padding-top:5px;}
.browser li.dropbox{background:url(http://dl.dropboxusercontent.com/u/56890834/.css/home.png)10px 5px no-repeat;padding-left:20px;padding-top:5px;}
.browser li.cur{background:url(http://dl.dropboxusercontent.com/u/56890834/.css/folder_in.png)29px 5px no-repeat;padding-left:39px;}
.browser .parent{padding-left:29px!important;background-position:19px 5px;}
.browser li.child{padding-left:29px!important;background-position:19px 5px;}  
.loading{background:rgba(0, 0, 0, 0.5);top:60px}
.jp-previous{background:rgba(0, 0, 0, 0) url(http://dl.dropboxusercontent.com/u/56890834/.css/video_rewind.png) no-repeat scroll left top;left:20px;}
.jp-play{background:rgba(0, 0, 0, 0) url(http://dl.dropboxusercontent.com/u/56890834/.css/video_play.png) no-repeat scroll left top;}
.jp-next{background:rgba(0, 0, 0, 0) url(http://dl.dropboxusercontent.com/u/56890834/.css/video_forward.png) no-repeat scroll left top;left:114px;}
.jp-pause{background:rgba(0, 0, 0, 0) url(http://dl.dropboxusercontent.com/u/56890834/.css/video_pause.png) no-repeat scroll left top;}    
.jp-play,.jp-pause{left:70px;}
.jp-next,.jp-previous,.jp-play,.jp-pause{height:35px;margin-top:-12px;position:absolute;width:34px;z-index:1;}

.tune-action,.tune-msg{margin-bottom:-5px;}
.tune-playnow,.tune-stop,.tune-msg{display:block;width:24px;height:24px;background:url(https://dl.dropboxusercontent.com/u/56890834/.css/play_toggle.png) 0px 0px no-repeat;}
.tune-playnow{background-position:0px -24px;}
.tune-stop{background-position:0px 0px;}
.tune-msg{background-position:0px -48px;}
}
