css:
@-moz-document domain("nyaa.se") {
/* Header */
#topbar{
    background: #ccc;
    box-shadow: 0px 0px 5px #555;
    z-index: 10;
    background: url(http://subtlepatterns.com/patterns/dark_embroidery.png);
}
#tabnav {
    margin: 0;
    padding: 0;
    margin-right: 20px;
}
#tabnav li {
    height: 40px;
    display: inline-block;
}

#tabnav a,
#tabnav a:link {
    font-weight: normal;
    color: white;
    padding: 10px;
    text-decoration: none;
    font-family: Verdana;
}

#tabnav a:hover { 
    background: black;
    opacity: 0.7; 
}

#searchcontainer select,
#searchcontainer input
{
    position: relative;
    top: 0px;
    padding: 3px 5px;
}

#searchcontainer input.inputsearchsubmit{
    border: 1px solid #aaa;
    background: #eee;
    padding: 4px 5px;
    cursor: pointer;
}

#searchcontainer input.inputsearchsubmit:hover{
    border: 1px solid #aaa;
    background: #f7f7f7;
    color: #555;
}

/* Content */
body
{
    background-image: url("http://th04.deviantart.net/fs71/PRE/f/2012/037/9/6/miku_hatsune_render_by_verosakagami-d4our8v.png"), url("http://fc03.deviantart.net/fs71/i/2013/004/c/3/sweet_render_by_bbernkastel-d5qfxqv.png"),url(http://subtlepatterns.com/patterns/concrete_seamless.png);
    background-repeat: no-repeat, no-repeat, repeat;
    background-position: right bottom, -100px bottom;
    background-attachment: fixed;
    background-size: 405px 597px, 525px 625px, 400px 400px;
}

#main, .content, .torrentcats{
    box-sizing: border-box;
    width: 100%;
    padding: 0;
    margin: 0;
    border: 0;
    border-radius: 0;
}

#main
{
    width: 1300px;
    margin: 0 auto;
    opacity: 0.95;
}

.content {
    width: 100%;
    margin-top: 60px;
    margin-bottom: 30px;
    padding: 20px;
    box-shadow: 0px 0px 10px #aaa;
    border-radius: 4px;
    background: #f7f7f7 url(http://subtlepatterns.com/patterns/crossword.png);
    position: relative;
}

.content .page,
.content .pages b
{
    border: 0;
    display: inline-block;
    width: 20px;
    height: 20px;
    padding-top: 6px;
    text-align: center;
}

.content .pages{
    width: 100%;
    position: relative;
    float: left;
    text-align: center;
    z-index: 1;
}

.content .rightpages, .content .leftpages{
    position: absolute;
    width: 200px;
    right: 20px;
    z-index: 3;
}

.content .rightpages{
    text-align: right;
}

.content .leftpages{
    text-align: left;
    left: 20px;
}

.content .leftpages span.page,
.content .rightpages span.page
{
    display: none;
}

.content .page{
    background: #fff;
    color: black;
    box-shadow: 0 0 1px #aaa;
}

.content .page:hover{
    background: #eee;
    box-shadow: 0 0 4px #aaa;
}

.content .pages b{
    color: rgb(136, 43, 43);
    font-size: 14px;
    font-weight: bold;
}

.content .tlistsortorder{
    margin: 10px 0;
    padding: 10px 0;
}

.content .tlistsortorder a{
    padding: 3px 5px;
}

.content .pages, 
.content .leftpages,
.content .rightpages{
    margin-top: 10px;
}

.content .tlist{
    margin-bottom: 15px;
    margin-top: 60px;
    margin-left: -20px;
    width: 1300px;
    border-spacing: 0;
}

.content .tlist th{
    padding: 5px;
    text-transform: uppercase;
    font-family: Verdana;
    font-size: 12px;
    border-bottom: 1px solid black;
    background: #f7f7f7 url(http://subtlepatterns.com/patterns/crossword.png);
}



.content .tlist tbody{   
    background: #f7f7f7 url(http://subtlepatterns.com/patterns/confectionary.png);
}

.viewfiletable,
.comment,
.viewdescription{   
    background: #f7f7f7 url(http://subtlepatterns.com/patterns/confectionary.png);
    border-width: 1.5px;
    border-color: #aaa;
}

td.fileentryname,
td.fileentrysize
{
    width: auto;
    float: none;
    padding: 3px 10px;
    border-bottom: 1px solid #ccc;
}

tr:last-child td.fileentryname,
tr:last-child td.fileentrysize:last-child{
    border-bottom: 0;
}

.content .tlist td{
    border-bottom: 1px solid black;
}

.content .tlist tr td:nth-child(1){
    padding-left: 10px;
}

.tlisticon, .tlistthone{
    padding: 3px 0;
    width: 90px;
}

.tlisticon img{
    width: 80px;
    height: 33px;
}

.tlistthfive, .tlistsn,
.tlistthsix, .tlistln,
.tlistthseven, .tlistdn
{
    text-align: right;
    padding: 3px 5px;
}
}
