css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

#dropmenu_container {
  width: 100% !important;
}

#menubar_container {
  width: 100% !important;
}

#fb_menubar {
  width: 100% !important;
  background: rgb(59,88,152) !important;
}
#fb_menubar_core {
  width: auto !important;
}

#fb_menubar_logo {
  display: none !important;
  visibility: hidden !important;
}

#content {
  width: 100% !important
}

.UIFullPage_Container {
  width: 98% !important;
}

.UIOneOff_Container {
  width: 100% !important;
}

.UIStandardFrame_Container {
  width: 98% !important;
}

.UIStandardFrame_Content {
  width: 98% !important;
}

.left_column_container {
  width: 88% !important;
}

#home_left_column {
  width: 80% !important;
}

#home_stream {
  width: 87% !important;
}

#home_sidebar {
  width: 18% !important;
}

#profile_top_bar {
  width: 97% !important;
}

.right_column_container {
  float: right !important;
  width: 83% !important;
}

.right_column {
  float: left !important;
  width: 100% !important;
}

#tab_canvas {
  width: 100% !important;
}

div.composer_rounded {
  background: rgb(240,240,240) !important;
}

div.UITwoColumnLayout_Content {
  width: 100% /*75%*/ !important;
}

div#home_main {
  width: 100% !important;
}

div.feed_item {
  width: 100% !important;
}

div.feed_story_wrapper {
  width: auto !important;
}
  
div.header_container {
  width: auto !important;
}

div.feed_story_body {
  width: auto !important;
}

div.feed_story_body div {
  width: auto !important;
}

div.UITwoColumnLayout_NarrowContent {
  display: none !important;
  visibility: hidden !important; 
}

div.UIComposer_UIRoundedBox {
  width: 75% !important;
}

div.RoundedBox_Box {
  background: rgb(240,240,240) !important;
}

}
