css:
.app-wrapper
{
	background-color:#0e6c80;
  	
  	
        background-image: url("http://i.imgur.com/FLObPmL.png") !important;
        background-size:auto;
        background-repeat:no-repeat;
}


.app-wrapper::before
{
	background-color:#282828;  
  	background-color:transparent;
  	
}


.pane-list-header 
{
	color:black;
}

.message-out:before
{
	background-image:none;
}
.message-out
{
		background-color:#c4e2ea;
}

.chat-title ,.header-title ,.location-main,.dropdown .menu-item a{color:#45A5D1!important;font-family:'Roboto',cursive !important}
.drawer-section-title .col-main,.drawer-title .drawer-title-body{color:#45A5D1!important;font-family:'Roboto',cursive !important}
.dropdown .menu-item a:hover{background:rgba(0,0,0,0.0);color:#A2ADB2!important;font-family:'Roboto',cursive !important}

.intro-image{background-image:url(http://i.imgur.com/3qYytAm.png) !important;background-size:cover!important;width:356px}
.pane-chat-body{background-image:url(http://i.imgur.com/mOUjk7b.png) !important;background-size:cover!important}


.drawer-container-right, .pane-chat, .pane-intro
{
	background-image:none;
  	background-color:lightgray;
}

#side, #main
{
	border-radius:3px;
}
#window, .app, .btn-more, .btn-plain:hover
{
	box-shadow:none;
}
.app, .two
{
	background-color:transparent;
    background-repeat:no-repeat;
  
}

.drawer-container-right, .pane-chat, .pane-intro
{
	margin-left: 40px;
    


}
.drawer-container-left, .pane-list
{
	width:349px;	
}
