css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubun2.d-lights.jp") {
/*======================================
  Ubuntuのある日々 ubun2.d-lights ja jp liquid compact 2015/05
  @-moz-document domain("ubun2.d-lights.jp")
  ======================================*/
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
#container {width: auto !important;}

/*--- header ---*/
#banner {margin: 0 !important; border-bottom: 2pt solid brown;}
#banner * {display: inline-block !important;}
#banner a img {height: 2em !important; width: auto !important;}
#banner input {max-width: none !important;}
#banner br {display: none !important;}

/*--- footer ---*/


/*--- main contents ---*/
#content {width: 100% !important; margin: 0 -180pt 0 0 !important; padding-left: 0.5em;}
#content>* {width: auto !important; margin: 0 180pt 0 0 !important;}
#links {width: 180pt !important; margin: 0 !important; padding-left: 0.5em !important; border-left: 1px dotted gray;}
#link * {max-width: 180pt !important;}

.navi {padding-bottom: 2pt !important;}
.date {padding: 0 !important;}
}
