css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("srad.jp") {
  /* 全体 */
 body{background-color: white !important;}
  *{border: none !important;}
  a, .link, a.link {color: #006666 !important;}
  *{box-shadow: none !important;}
  h2{background-color: #004242 !important;
  color: white !important;}
  
  
  #logo a{visibility: hidden !important;}
  #logo{
    background-color: #026664 !important;
    background-image:url('http://wiki.neurostechnology.com/images/4/4c/Slashdot_logo.gif') !important;
  background-repeat: no-repeat;}
  section.header-menu{background-color: #004242 !important;}
  section.header-menu a{color: white !important;}
  
  div.col_1{background-color:#e5e5e5 !important; }
  h2.story{border-top-left-radius: 10px !important;
    background-image: -moz-linear-gradient(100% 100% 90deg, #004242, #006666 100%);
  background-color: #004242 !important;}
  article h2 a {color: white !important;}
  article .details{background-color: #e1e1e1 !important;;
  color:#4d4d4d !important;}
  article{box-shadow: none !important;}
  article .topic{
    float: right !important;
  padding-right: 8px !important;}
  article .topic img{
    padding-top: 8px !important;
    width: 80% !important;
  height: auto !important;}
  
  #commentlisting li h4 {background-color: #004242;
    background-image: -moz-linear-gradient(100% 100% 90deg, #004242, #006666 100%);
    border-top-left-radius: 10px;
  }
   #commentlisting li h4 a {color: white !important;}
  
  .thisday-tb tr td.thisday-yr{background-color: #004242 !important;}
}
