css:
@-moz-document domain("nnm-club.me"), domain("nnmclub.to") {
body {
    background: #fff !important;
    padding: 0 !important;
}

body, font, th, td, p, input, select, textarea, button, .nav {
    font-family: Roboto,Tahoma,Arial,sans-serif;
    font-size: 11px;
}

[name^="dl_set_"] {
    width: 100px;
}

a[style="font-size:8pt"]{
    font-size: 12px !important;
}

.tg {
    padding-left: 0;
    padding-right: 0;
}

.menutable table td:hover {
  	background: #469;
}

.header input[name="nm"] {
    position: fixed;
    top: 2px;
    width: 320px !important;
    margin: 0 !important;
    left: calc((100vw - 320px) / 2);
    z-index: 1;
}

#adss, #cse-search-box {
    display: none;
}   

body > div[style="display:none;"] {
    position: fixed;
    display: block !important;
    top: 0;
    width: 1225px !important;
    margin: 0 !important;
    left: calc((100vw - 1225px) / 2);
    opacity: 1 !important;
    bottom: auto !important;
    background: #69c !important;
    height: 23px;
    border-bottom: #469 solid 1px !important;
}

body > div[style="display:none;"]:before {
    content: "Поиск:";
    color: white;
    font-family: inherit;
    position: absolute;
    left: 408px;
    top: 4px;
}

.wrap {
    margin-top: 24px;
}

.menutable {
    background: none;
    width: 1200px;
    height: 50px;
}

.menutable table {
    width: 1200px;
}

.menutable td {
    height: 20px;
}

.forumline td[width="150"] *, .postbody, .quote {
    font-size: 11px;
}

.forumline td[width="150"] {
    position: relative;
}

.posterAvatar {
    position: absolute;
    right: 5px;
    top: 5px;
}

.posterAvatar img {
	max-width: 40px !important;
	max-height: 40px !important;
}

#post_body {
    height: 70px;
    resize: none;
}

.forumline hr {
    margin: 2px 0;
    border-color: #c6d9ec !important;
}

td > table[height="18"] img, img[title="Ответить с цитатой"] {
    height: 15px !important;
}

td[height="28"] {
    height: 15px !important;
}

#mb_top_space, __table.header > tbody > tr + tr, td[height="75"] > table > tbody > tr > td:first-child, form[name="search_block_top"], a[href^="rss.php?"], .menutable + div, div.copyright tr:nth-child(2), #topcontrol, #livi, .forumline td[width="150"] > div img:not([src^="http://assets.nnm-club.ws/forum/images/avatars/"]), .rank_progress_bar.tit, .signature, iframe[scrolling="NO"], td:first-child[style="letter-spacing: 1px"], [href="#top"], .btTbl .rowlightgreen:last-child, .btTbl tr.row1:nth-last-child(2), table.tg>tbody>tr+tr>td>table>tbody>tr>td>table:nth-last-child(3), table.tg>tbody>tr+tr>td>table>tbody>tr>td>table:last-of-type, td.copyright, table.qt + br, form > table td[width="30%"], form > table td[width="40%"] > :not(.gen), [onclick="spellCheck()"], [onclick="speller.optionsDialog()"], #post_opt, [id^="skin_block"], .skin-block, body > .request[style="display: block;"], [id^="MarketGidComposite"], #adss *, #adb, [id^="M"][id*="Composite"], body > noindex:first-child {
    display: none !important;
}
}
