css:
@-moz-document domain("www.ted.com") {
html, body, div.page, div.home-about, div.banner, ul.nav__menu, li.nav__item, a.nav__link, div.nav__folder, header.banner, div.controls {
 background:#2f2f2f !important;
 color:#b9b9b9 !important;
}

div.controls--fullscreen {
 background:transparent !important;   
}

a, h1, h2, h3, h4, div.h1, div.h2, div.h3, p, ul, div, span {
 color:#b9b9b9 !important;
}

div.banner {
 border-top: solid red 1px !important;
 border-bottom: solid red 2px !important;
}

div.talk-section--borderless {
 display:none !important;   
}
}
