css:
@-moz-document domain("duckduckgo.com") {
body, #pg-index {
    background-color:transparent !important;
	background-image:url("http://e-shuushuu.net/images/2013-12-02-620835.jpeg") !important;
	background-attachment:fixed !important;
	background-position:center center !important;
	background-repeat:no-repeat !important;
    background-size:auto 100% !important;
}

div.site-wrapper, div#header_wrapper, div.header:after, .header-wrap:after, div.zci, div.zci__metabar-wrap, div.search__autocomplete {
	background:transparent !important;
    border:none !important;
}

div#zero_click_wrapper, div#did_you_mean {
 	border:none !important;
    background:rgba(33,33,33,0.6) !important;
}

div#did_you_mean {
 	text-shadow:1px 0 0 #333333 !important;   
}

a#logo_homepage_link, div.tag-home, a.result__check, span.header__logo {
 	visibility:hidden !important;
}

div#links {
    margin:auto !important;
    background:rgba(33,33,33,0.6) !important;
}

div.tile, form#search_form_homepage {
 	background:rgba(33,33,33,0.6) !important;   
}

div.highlight {
    background:rgba(33,33,33,0.2) !important;
}

.zci:before {
 	display:none !important;   
}

.header-wrap--home, div.site-wrapper-border {
 	border:none !important;
    display:none !important;
}

div#duckbar {
 	background:#333333 !important;   
}

.zcm-wrap--header {
 	margin-left:0px !important;   
}

.search-wrap--home {
 	max-width:3840px !important;
    width: 90%;
}

.cw--c {
 	max-width:3840px !important;   
}

#search_form_homepage {
 	border-color: #A00000 !important;   
}

#header, .header__search-wrap {
 	max-width: 97% !important;   
}

#search_form {
	padding-right: 0px;    
}

div.feedback-btn {
 	display:none !important;   
}
}
