css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://MYURL.harvestapp.com/") {
  body, #main_content, #sub_nav, .nav, .clearfix, nav #sub_nav, .js-nav, .main-navigation, #global-header, .footer, #global-footer {
    background-color: #222 !important;
    color: #eee !important;
  }
  .running, .day-view-entry td, .empty-view {
     background-color: #333 !important;
    color:white !important;
    
  }
  .js-day, .test-not-today, .day-view-week-nav  {
    background-image:  linear-gradient(#444, #333) !important;
    color: white !important;
  }
     .today  {
    background-image:  linear-gradient(#228, #333) !important;
    color: white !important;
  }
       .selected  {
    background-image:  linear-gradient(#882, #333) !important;
    color: white !important;
  }
}
