css:
/*
正则表达式: ^http(s)?://www.google.com/bookmarks/(.+)?
*/
@-moz-document domain(google.com) {
/* 列表 */
.result td{color: #999}
.result .jfk-star{opacity: .6}
.result tr td:nth-child(2) > span:nth-child(1) > a{font-size: 16px}
.result a{color: #4d90f0}

#search > div{padding: 5px 0; border-bottom: 1px solid #fafafa; border-top: 1px solid #fff}
#search > div:hover{border-bottom: 1px solid #4d90f0; border-top: 1px solid #4d90f0; background: #EAF3FF}
#search > div:hover td{color: #333}
#search > div:hover .jfk-star{opacity: 1}

/* 导航 */
#sidenav{width: 100% !important; right: 0; left: 0;}
.kd-content-sidebar li:nth-child(2){display:block}
.kd-content-sidebar{padding: 0; width: 60%; margin: auto}
.kd-content-sidebar li,
.kd-content-sidebar li a{display: inline-block; line-height: 14px}
.kd-content-sidebar li{margin: 0}
.kd-content-sidebar li a{margin: 2px 4px; padding: 2px; border-radius: 3px}
.kd-content-sidebar li a:hover{background-color: #4d90f0; color: #fff}
.kd-content-sidebar bdo{font-size: 10px; color: #999}
.kd-content-sidebar li a:hover bdo{color: #eee}
.kd-content-sidebar li.selected a{font-size: 16px; font-weight: 700}

/* 布局 */
#cnt{margin: 0 auto; max-width: 100%; width: 100%}

/* 列表 */
#center_col{margin-left: 0; padding-top: 120px; width: 60%; margin: 0 auto}

@media only screen and (min-width: 1222px){
    #sidenav{margin: 0}
}

}
