.c-box input[type="checkbox"]+label{
    background:none;
   background-position:0px 0px;
   background-color: white;
}
.c-box input[type="checkbox"]:checked + label{
   background-position:0px 0px;
   background-color: lightseagreen;
   border-color:lightseagreen;
   
}
.c-box  label {
	cursor: pointer;
	position: absolute;
	width: 13px !important;
  height: 13px !important;
   top: 0;
   border-radius: 22%;
  right: 6px;
  border: 1px solid gray;
}
.c-box label:hover {
   background-color: lightseagreen !important;
  border-color: lightseagreen !important;
   
}
.c-box  label:after {
	
	opacity: 0;
	content: '';
	position: absolute;
	width: 8px;
  height: 4px;
   background: transparent;
   top: 1px;
   left: 1px;
	border: 3px solid #fff;
	border-top: none;
	border-right: none;
	transform: rotate(-45deg);
   
}
.c-box  label:hover::after {
	opacity: 0.5;
}

.c-box  input[type=checkbox]:checked + label:after {
	opacity: 1;
}
