css:
@-moz-document
url-prefix("https://userstyles.org/")
{
  body
  {
    background-color: #1C1C1C !important;
    background-image: url("data:image/png;base64,") !important;
    color: #FAFAFA !important;
  }
  
  #nav-view, #navigation
  {
    background: transparent !important;
  }
  
  #footer
  {
    background: #161616 !important;
    border-top: solid #CCCCCC 1px !important;
  }
  
  a, a:visited, #title_long a, #title_long a:hover, #title_long a:visited
  {
    color: #0973BF !important;
  }
  
  .navigation_link:hover
  {
    background: #2C2C2C !important;
  }
  
  h1, h2, h3, h4, h5, h6
  {
    color: #FAFAFA !important;
  }
  
  #copyrights, #headline, .style_title, .navigation_title, .style_author_long
  {
    color: #FAFAFA !important;
  }
  
  #share_div, #information_key_left
  {
    color: #CCCCCC !important;
  }
  
  #infomation_value_left, #applies_link, .text_style_page, #information_key, #information_value
  {
    color: #FAFAFA !important;
  }
  
  #css_button, #send_feedback_button, .see_more, .div-arrow, .white_button, .css_button, .advanced_button
  {
    background: linear-gradient(#3C3C3C, #2E2E2E) !important;
    border: solid white 1px !important;
    color: #0973BF !important;
  }
  
  #archive_style_button
  {
    background: linear-gradient(#3C3C3C, #2E2E2E) !important;
    color: #A832B2A !important;
    border: solid 1px #A832B2A !important;
  }
  
  #style_installed_button
  {
    background: linear-gradient(#3C3C3C, #2E2E2E) !important;
    color: #2ECC71 !important;
    border: solid 1px #2ECC71 !important;
  }
  
  .view_details
  {
    background: linear-gradient(#3C3C3C, #2E2E2E) !important;
  }
  
  .advancedsettings_shown
  {
    background: #1C1C1C !important;
  }
  
  .title_setting, .upload_image_label
  {
    color: #FAFAFA !important;
  }
  
  .option-user-url
  {
    background: #2C2C2C !important;
    color: #FAFAFA !important;
  }
  
  .style_card
  {
    background: #3C3C3C !important;
  }
  
  h4 a, h4 a:visited
  {
    color: #0973BF !important;
  }
  
  #notes_textarea
  {
    background: #2C2C2C !important;
    color: #FAFAFA !important;
  }
  
  .eye_image
  {
    filter: invert(100%);
    -webkit-filter: invert(100%);
  }
  
  #navigation > .linksBox:nth-child(3n+1) > a > .navigation_link  > img, #navigation > .linksBox:nth-child(5n) > a > .navigation_link  > img
  {
    filter: invert(100%);
    -webkit-filter: invert(100%);
  }
}

@-moz-document
url-prefix("https://forum.userstyles.org")
{
   body
  {
    background-color: #1C1C1C !important;
    background-image: url("data:image/png;base64,") !important;
    color: #FAFAFA !important;
  }
  
  a, a:visited
  {
    color: #0973BF !important;
  }
  
  a:hover
  {
    color: #0973BF !important;
    text-decoration: underline;
  }
  
  #Head
  {
    background: #0973BF !important;
  }
  
  .CommentForm textarea.TextBox, .Attachment:hover .FileHover, input.InputBox, input.InputBox:focus, textarea, textarea:focus
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
    border: solid white 1px;
  }
  
  div.Popup .Body
  {
    background: #0A0A0A !important;
    color: #FAFAFA !important;
  }
  
  a .TextColor, a.Close
  {
    color: #FAFAFA !important;
  }
  
  code, pre
  {
    background: #0A0A0A !important;
  }
  
  .Item.Read
  {
    background: #0A0A0A !important;
    border-bottom: solid white 1px;
  }
  
  blockquote.Quote, blockQuote.UserQuote
  {
    background: #0A0A0A !important;
    border-left: solid white 2px;
  }
  
  .Message small
  {
    color: #CCCCCC !important;
  }
  
  .Pager .Highlight, #Head a
  {
    color: #FAFAFA !important;
  }
  
  .Button, .atwho-view
  {
    background: transparent linear-gradient(#3C3C3C, #2E2E2E) repeat scroll 0% 0% !important;
    color: #FAFAFA !important;
    text-shadow: none !important;
    box-shadow: none !important;
  }

  .SiteSearch .Button
  {
    background: url("https://forum.userstyles.org/applications/dashboard/design/images/sprites.png") 0 -196px no-repeat transparent !important;
  }

  .ReactSprite, .OptionsTitle, .Sprite16, .SiteSearch .Button
  {
    filter: invert(100%);
    -webkit-filter: invert(100%);
  }
  
  a.Bookmark
  {
    opacity: 0.8 !important;
  }
  
  a.Bookmark:hover
  {
    opacity: 1 !important;
    background-position: 0px -2px !important;
  }
  
  .Preview
  {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
  }
  
  ul.token-input-list, div.token-input-dropdown, div.token-input-dropdown ul li.token-input-dropdown-item, div.token-input-dropdown ul li.token-input-dropdown-item2
  {
    background: #161616 !important;
    color: #FAFAFA !important;
  }
  
  div.token-input-dropdown ul li.token-input-selected-dropdown-item
  {
    background: #1C1C1C !important;
  }
}
