@-moz-document domain("www.birdz.sk") {  
.page {
  display:block;
  margin:0 auto;
  overflow:visible;
}

.page .in {
  width:75vw;
  display:block;
}

a, a:link, a:active, a:visited,h1.strongh span {
  color:#e50056;
}

a:hover {
  color:#bb0046;
}
  
/* bb box */
#menu ul li a,
.ic em,
.bts textarea.texta,
.tologinbox li a,
.statusitem,
.contnrLeft,
.contnrRight,
.contnrMiddle,
.gridforum,
.gridblogy,
.gridfotky,
.gridblogy,
.pridajnovinku #pridajstatus,
.newsfeed li img.avatar,
.newsfeed li .status,
