}

#menu li.vyh,
.tcv,
.contnrLeft strong.h2 a,
.covermenu a.close {
  display:none;
}

#menu li a,
#menu li.frst,
.loggedbox,
.loggedbox a.av,
.loggedbox:hover a.av,
#menu li.last,
.gridforum,
.tologinbox li a {
  border:none;
  height:auto;
}
  
.tologinbox  {
  border:none;
  height:100%;
}

.usermenu li, .darkmenu li {
  border:none;
}

.hovered {
