}

.darkmenu a, .darkmenu li a, .hovered a, .usermenu a,.hovered li a,.usermenu li a,.loggedbox:hover,.loggedbox:hover a.av strong {
  color:#666666 !important;
}

.darkmenu li,
.usermenu li {
  padding:3px 5px;
}

.darkmenu li a img {
  margin-top:10px;
  border-radius:20px;
}

#header .in,
#menu {
  height:100%;
}

#menu ul {
  display:block;
  width:auto;
  margin-left:30px;
  height:100%;
}
  
.tologinbox ul {
  display:block;
  width:auto;
  height:100%;
}

#menu ul li,
.tologinbox ul  li {
  height:100%;
