  left:260px;
}

.bmoj ul.threads  {
  background-color:#ffffff;
  margin-top:7px;
  border-radius:40px;
  overflow:hidden;
  border: 1px solid #E5DED4;
}
  
.bforum .threads li {
  border-radius:40px;
  overflow:hidden;
  margin:10px 0;
}

.bmoj ul.submenufeed.threads {
  margin-top:50px;
}

ul.threads li a,.threads li.otvorena strong, .messages li.otvorena strong, div.otvorena strong,ul.threads li a:hover {
  color:#666666;
}

ul.threads li a {
  padding:5px 0;
  border-bottom: 1px solid #E5DED4;
}

.bmoj ul.threads li:hover {
  background-color:#eaeaea;
}
  
ul.threads li:hover {
