}

.contnrLeft {
  width:15%;
}

.contnrRight {
  width:25%;
}
  
.contnrLarge {
  width:82.5%;
  margin-left:2.5%;
}

.contnrMiddle {
  width:55%;
  margin-left:2.5%;
}

.bts .contnrMiddle {
  width:79%;
  margin-top:10px;
}

.contnrLeft {
  padding:18px 20px 0 0;
}
.page {
  padding:0;
}

#rightcolumn .grid {
  width:auto;
}

.gridforum,
.gridblogy,
.gridfotky,
.gridblogy {
