}

.contnrRight .paf1p,
.contnrRight .paf2p,
.contnrRight .paf3p,
.contnrRight .paf4p {
  margin-top:35px;
}

.generatedfeed ul li,
.newsfeed li,
.generatedfeed .pridajnovinku {
