  padding:10px;
}

.pridajnovinku #pridajstatus  {
  width:82%;
  margin:10px 2vw 20px 0;
  display:block;
  float:right;
  border-radius:20px;
  overflow:hidden;
  height:12vh;
  padding:10px 15px;
}
  
textarea.input {
  padding:10px 15px !important;
}

.pridajnovinku input.largeButton,
a.largeButton,
input.largeButton {
