  background-color:#e50056;
  margin-right:2vw;
  margin-bottom:10px;
}

.pridajnovinku input.largeButton:hover,
a.klikpridaj.largeButton:hover,
input.largeButton:hover {
  background-color:#bb0046;
}

.newsfeed li img.avatar {
  width:12%;
  height:auto;
  margin:5px 0 0 0;
  padding:0;
  border-radius:150px;
}

.pridajnovinku img.avatar {
  width:10%;
  height:auto;
  margin-top:5px;
  padding:0;
  border-radius:150px;
}

.newsfeed li .status,
.newsfeed li .fotostatus_new {
  width:87%;
  padding:0.5vw 1vw 0.5vw 0.5vw;
  float:right;
}

.fotostatus_new a {
  width:auto;
}

.newsfeed li .fotostatus_new img {
  width:auto;
  max-width:97%;
