  margin:0 auto;
  border-radius:20px;
}

.newsfeed li .statusinside {
  border-top:1px #eaeaea solid;
  display:block;
  width:100%;  
}

.newsfeed li .statusinside p {
  width:88% !important;
  display:block;
  float:right !important;
}

.newsfeed li .statusinside .avatar {
  margin:5% 2% 1% 0;
  width:10% !important;
  height:10% !important;
}

.bmoj .generatedfeed {
  margin-top:20px;
}

#feed .h1smaller {
  position:relative;
  top:-20px;
}

#switch {
  border-radius:20px;
  overflow:hidden;
  background:#ffffff;
  border: 1px solid #E5DED4;
}

#switch li a:hover {
  background-color:#cacaca;
}

.gridfoto li {
  margin:0;
  padding:0;
  width:33.333%;
  overflow:hidden;
  border:1px #ffffff solid;
}

.gridfoto li a img {
  width:100%;
  height:100%;
}
  
.fotostatus_new span {
  width:97%;
}
  
.covermenu {
  border-radius:20px;
  overflow:hidden;
}

.friendmenu {
  display:none;
}

.covermenu .submenu li {
  width:16.666%;
  text-align:center;
  border-left:1px #eaeaea solid;
}

.covermenu .submenu li:first-child {
  margin-left:-11px;
}
  
.bwebka .middle {
  width:100% !important;
  border:none;
}

.webkaArticles .articles li {
  padding:10px;
}
  
#pridajkoment.comment {
  overflow:hidden;
}
  
#menu a.s,
.tologinbox li a.s {
  padding:0px 9px;
  background:#666666 !important;
}

.covermenu .submenu li a {
  color:#e90157;
}

.covermenu .submenu li a:hover {
  color:#bb0046;
  background:none;
}

.bwebka #pridajnovykoment,#pridajnovykoment {
  width:100%;
  padding:10px 15px;
  border-radius:20px;
}
  
.page .pi {
  border-radius:20px;
  padding:1vw;
}

a.prevPhotoArrow {
  border-radius:0 20px 20px 0;
}

a.nextPhotoArrow {
  border-radius:20px 0 0 20px;
}

.ccomment {
  width:85%;
  float:right;
  padding-left:10px;
}

.bwebka .ccomment {
  width:87%;
}

.comment {
  padding:10px;
}

.ccomment iframe,
.statusquo iframe {
  width:100%;
  height:60vh;
}
  
.status iframe {
  width:100%;
  height:50vh;
}

.comment .cavatar {
  width:15%;
  height:auto;
  padding:0;
  margin-top:15px;
}

.bwebka .comment .cavatar {
  width:12%;
}

.comment .cavatar img.avatar {
  width:100%;
  height:auto;
  border-radius:200px;
}
  
.comment em {
  top:10px;
  right:10px;
  left:auto;
}

.zareaguj {
  border-radius:10px 0 0 0;
  background-color:#e70056;
  color:#ffffff !important;
}

.zareaguj:hover {
  background-color:#bb0046;
}
  
.newsfeed .person {
  padding-left:2.3%;
}
  
.contnrLeft {
  width:15% !important;
  padding:0;
  margin:0;
  float:left;
}

.bts .threads {
  background-color:none;
  overflow:hidden;
  border:none;
}

.userleftmenu li.otvorena,
.bts .threads li.otvorena {
  background:#eaeaea !important;
  color:#bb0046;
}

.bts .threads li.otvorena strong {
  color:#bb0046;
  font-weight:600;
}
  
.threads .date {
  display:block;
  clear:both;
  width:100%;
  text-align:right;
  margin-right:10px;
}

.userleftmenu li {
  border:none;
  text-align:center;
}

.userleftmenu li:hover,
.threads li:hover {
  background:#fafafa;
  color:#bb0046;
}

.threads a.amsg {
  padding:10px;
}
  
.threads li.nova, .messages li.nova, div.nova {
  background-color:#dadada !important;
}
  
#contnr {
  margin-top:20px;
}
  
 #contnr.left.containerFix.r {
  width:100%;
}

#contnr.left.containerFix.r .contnrLeft {
  width:15%;
  overflow:hidden;
}

#fixmsgs.fixed {
  position:initial;
  float:right;
  width:100%;
  height:auto !important;
  margin-top:30px;
  border-radius:20px;
  overflow:hidden;
  background-color:#ffffff;
  overflow:hidden;
  border: 1px solid #E5DED4;
}

.odtrhnuty {
  display:none;
}

.mainMsgsShow .mainMsgsIn {
  width:100%;
  border-radius:20px;
  padding:15px;
}

.wholeThread {
  width:100%;
  height:auto !important;
  clip:initial;
  overflow:hidden;
}

.bts .contnrMiddle {
  height:auto;
}
  
.wholeMessage img.avatar,
.replyMessage img.avatar {
  width:10%;
  height:10%;
  margin:0;
  padding:0;
  border-radius:50%;
}

.messageText {
  width:87%;
  margin:0;
  padding:0;
  float:right;
}
  
#contnr.left.r {
  width:100%;
}
  
.bforum .middle {
  width:70% !important;
}

.contnrLeft strong.h2 {
  text-align:center;
}
  
.containerFix.p {
  border:none;
  border-radius:20px;
  padding-top:10px;
  border: 1px solid #E5DED4;
}

.containerBoth .contnrMiddle {
  width:75%;
  margin:0;
}

.bforum .ccomment {
  width:84%;
  padding:7px 30px 7px 15px;
}

.grid, .gridlarge {
  border:none;
} 
  
.redbubble, a.blackbubble:hover, a.scroll_up:hover {
  background-color:#e50056;
}
  
.fotostatus {
  clear:none;
  float:right;
  width:85%;
  margin:0;
  padding:10px 0 20px 0;
}
  
.fotostatus img {
  border-radius:20px;
}
  
.newsfeed .largePhoto {
  border:none;
  display:block;
  margin:4px 0 20px 4%;
  border-radius:20px;
  float:right;
}
  
a.logo {
  background: url("http://birdz.net84.net/s.png") no-repeat scroll -6px 0px transparent;
}

.ic a {
  background: url("http://birdz.net84.net/s.png") no-repeat scroll -282px -2px transparent;
}
  
.pagination3 {
  width:100%;
  margin-bottom:30px;
}
.bblogy .pagination3,.bnastenka .pagination3,.bforum .pagination3,.bwebka .pagination3 {
  width:200px;
}

.photo_timeline li {
  width:15.66%;
  text-align:center;
  font-size:15px;
  opacity:0.9;
  transition:0.6s;
  margin:0.5%;
  padding:0;
}

.photo_timeline li:hover {
  opacity:1.0;
  transform:scale(1.05,1.05);
}

.photo_timeline li img,
.photos li a img {
  width:100%;
  height:100%;
  border-radius:20px;
  border:2px #ffffff solid;
}

.photo_timeline li .boxed {
  background:none;
  margin:5px 0 10px 0;
  font-weight:bold;
  display:block;
}

.photo_timeline li:nth-child(4) {
  display:none;
}

.photo_timeline li:hover .boxed {
  background:none;
}
  
.pagination3 strong,
.pagination3 a {
  width:2vw;
  height:2vw;
  display:inline-block;
  margin:0;
  padding:0;
  text-align:center;
  line-height:2vw;
  font-size:1vw;
  border-radius:1vw;
}

.pagination3 a:hover {
  background-color:#d13867;
  color:#ffffff;
  box-shadow:none;
  border:1px #9b1941 solid;
}
  
.contnrLarge.p.grid {
  border-radius:20px;
  padding:10px 0;
  margin-top:30px;
  border: 1px solid #E5DED4;
}

.left.middle.pi.c {
  width:60% !important;
  margin-right:3%;
}

.bforum .submenu li a, .bforum .wlist li a {
  padding:8px 0;
}

.containerRight {
  float:right;
  margin:0;
  width:35% !important;
}

.containerRight .grid {
  width:90% !important;
}
  
.bforum .contnrMiddle,.statusitem .status {
  width:70%;
}
  
.bforum .containerRight {
  width:28% !important;
  margin-right:1%;
  display:block;
  height:auto;
}

#fixovanyWraper {
  width:28%;
  display:block;
}
  
.bubble1b {
  right: 25%;
  top: -30px;
  left: auto;
}

.side .td {
  background:none;
}

.bludia .contnrMiddle {
  width:79%;
}

.cards li {
  width:20%;
}
  
.statusitem {
  border-radius:38px;
  padding:5px 10px 10px 5px;
  width:300px;
}
  
