  background:#ffffff;
  padding:0 10px 10px 0;
  overflow:hidden;
  border-radius:100px;
  height:100px;
  border: 1px solid #E5DED4;
}

.bnastenka .statusitem .avatar {
  border-radius:100px;
}

.bnastenka #pridajkoment .avatar {
  width:6%;
  height:auto;
  border-radius:100px;
  margin:0.6% 0 0 0.6%;
}

.bnastenka #pridajkoment textarea {
  position:absolute;
  left:8.5%;
  width:72%;
  height:60px;
  margin-top:0.6%;
  border-radius:30px;
}

.bnastenka #pridajkoment input.largeButton {
  top:24%;
  width:12%;
}  

.bts .contnrLeft .avatar {
  border-radius:100px;
}

.bts .threads li {
  margin:10px 0;
  background:#ffffff;
  padding:2px;
  border-radius:30px;
  border: 1px solid #E5DED4;
}

.bts .threads li:hover {
  padding:2px;
}

.replyMessage .messageText {
  width:88%;
}

.bts textarea.texta {
  width:99% !important;
  border:none;
  border-radius:30px;
  padding:10px;
  box-shadow:none;
  height:80px;
}

.replyMessage {
  box-shadow:none;
  border-radius:50px;
  background-color:#dadada;
  margin-top:20px;
}

div.nova {
  background:#eaeaea;
  border-radius:50px 0 0 50px;
}

#komentovat .largeButton {
  margin:20px auto 20px auto;
  position:relative;
  top:20px;
  display:block;
  clear:both;
  float:none;
}

.photos li {
  width:20%;
  height:auto;
}

.photos li a {
  width:100%;
  height:auto;
}

.ic em {
  background:none;
  background-color:#ffffff;
  color:#666666;
  border-radius:20px;
  border:2px #666666 solid;
  line-height:1;
}

a.close:hover {
  background-position:-187px -43px !important;
}
  
.bforum .articles a strong, .bforum .articlesgrid a strong,.bnastenka .status a {
  color:#666666;
}

@media all and (max-width:1500px) {
  .page .in {
    width:80vw;
  }
  
  .contnrRight {
    width:30%;
  }  
  
  #contnr.left.r .contnrLeft {
    width:20% !important;
  }
  
  .contnrMiddle {
    width:48%;
    margin-left:1%;
  }
  
  .cards li {
    width:25%;
  }
  
  .contnrLarge {
    width:77.5%;
    margin-left:2.5%;
  }
}

@media all and (max-width:1366px) {
  .page .in {
    width:85vw;
  }
  
  .pagination3 strong,
  .pagination3 a {
    width:30px;
    height:30px;
    font-size:15px;
    border-radius:15px;
    line-height:2;
  }
  
  .photo_timeline li {
    width:17.9%;
  }
  
  .photos li {
    width:25%;
  }
}

