  {
    font-size:29px !important;
    line-height:1.5;
  }
  
  .usermenu li a, .darkmenu li a,#menu li a,.largeButton,strong.h2, h1.h2
  {
    font-size:30px !important;
  }
  
  .bforum .articles a strong, .bforum .articlesgrid a strong,.td span
  {
    font-size:35px;
  }
  
  .userstats,.usercover strong.h2,.mojesiete2 td,.userstats span,.cards li,.bblogy .article, .beditovat .article, .bwebka .article,.hviezdicky li.info,.usercard,.bts .threads li span.date,.messages li span, .wholeMessage span.date
  {
    font-size:20px !important;
    line-height:1.8;
  }
  /**/
  
  .darkmenu {
    display:none !important;
  }
  
  #menu ul li a:hover, .tologinbox li a:hover, .usermenu,.loggedbox:hover {
    background:none !important;
  }
  
  a.logo {
    transform:scale(1.8);
    top:-35px;
    left:10%;
  }
  
  .page {
    width:95vw;
  }
  
  .page .in,.webkaPhotos li a,.webkaPhotos li a img {
    width:100%;
    height:auto;
  }
  
  .gridfoto li {
    width:33%;
  }
  
  #header {
    height:auto;
    position:absolute;
    overflow:visible;
    background:none !important;
    box-shadow:none;
  }
  
  #menu {
    width:auto;
    display:block;
