css:
@-moz-document url-prefix("http://www.youtube.com/watch?"),
url-prefix("https://www.youtube.com/watch?")
{
  body
  {
  position: fixed !important;
}

 html
{
  
background: rgb(27,27,27) !important;
 overflow: hidden !important;
  
}
html .watch-stage-mode #player,
html .watch-non-stage-mode #player,
html .watch-stage-mode .player-width,
html .watch-non-stage-mode .player-width {
position: static !important;
  width: 100% !important;

  width: 100vw !important;

  margin-left: 0 !important;

  left: 0 !important;
  

}


  html .watch-non-stage-mode .player-height, .ytp-upnext{
 top: -10px !important;
   margin-top: 0 !important;
    padding-top: 0 !important;
   bottom: 0 !important;
   margin-bottom: 0 !important;
    padding-bottom: 0 !important;
  height: 95vh !important;

  height: calc(100vh - 0px) !important;  /* https://code.google.com/p/chromium/issues/detail?id=168840 */

}

html .watch-stage-mode .player-height {
 top: -10px !important;
   margin-top: 0 !important;
    padding-top: 0 !important;
   bottom: 0 !important;
   margin-bottom: 0 !important;
    padding-bottom: 0 !important;
  height: 95vh !important;

  height: calc(100vh - -10px) !important;  /* https://code.google.com/p/chromium/issues/detail?id=168840 */

}

  

html .watch-stage-mode .ad-container,
html .watch-non-stage-mode .ad-container,
html .watch-stage-mode .html5-video-container,
html .watch-non-stage-mode .html5-video-container,
html .watch-stage-mode video.video-stream.html5-main-video,
  html .watch-non-stage-mode video.video-stream.html5-main-video,
  .ytp-storyboard-framepreview-img, .html5-video-content{
position: fixed !important;
  left: 0 !important;

  top: 0 !important;

  width: 100% !important;

  height: 100% !important;

}



html .watch-stage-mode .html5-video-container > .video-ads > .ad-container,
  html .watch-non-stage-mode .html5-video-container > .video-ads > .ad-container{

  /* Fix for ads bottom bar at the old player. */

  bottom: 30px !important;

  height: auto !important;

}

.ytp-chrome-bottom,   .html5-video-content,
 .ytp-player-content, .ytp-progress-bar-container, .ytp-storyboard-framepreview, ytp-progress-bar{
  
  width: 43.8% !important;
position: fixed !important;
margin-left: auto !important;
margin-right: auto !important;
  margin-right: auto !important;
left: 0px !important;
  right: 0px !important;
padding-left: 0px !important;
  padding-right: 0px !important;
}
  
.ytp-panel-content, .ytp-panelpopup, .ytp-settings-menu{
 
  margin-left: auto !important;
margin-right: auto !important;
  margin-bottom: auto !important;
left: 0px !important;
  right: 0px !important;
padding-left: 0px !important;
  padding-right: 0px !important;
    padding-bottom: 0px !important;
}


  .html5-video-controls, .ytp-chrome-controls
{
pointer-events:all !important;
bottom: 28px !important;
-moz-transition-property: all  !important;
-moz-transition-duration: 1s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1s !important;
}
  
 .html5-video-controls:hover, .ytp-chrome-controls:hover
{

padding-bottom: 28px !important;

}
  


.playlist-videos-list
{
padding-bottom: 210px !important;
width: 100% !important;
}

#watch-appbar-playlist
{
  pointer-events:all;
  margin-top: 0px !important;
position: fixed !important;
top: 0px !important;
left: -6px !important;
width: 0px !important;
max-width: 450px !important;
border: 0px !important;
height: 50% !important;
padding-left: 7px !important;
  padding-bottom: 220px !important;
background: rgb(43, 43, 43) !important;
overflow: hidden !important;
-moz-transition-property: all !important;
-moz-transition-duration: 1.3s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1.3s !important;
}

#watch-appbar-playlist:hover
{
left: 0px !important;
max-width: 450px !important;
width: 99% !important;
height: 50% !important;
}

#watch7-user-header, #watch7-views-info
{
padding-top: 15px !important;
}

#watch7-content
{
  pointer-events:all;
position: fixed !important;
top: 0px !important;
right: -6px !important;
width: 0px !important;
max-width: 650px !important;
border: 0px !important;
height: 90% !important;
padding-left: 7px !important;
padding-right: 0px !important;
background: rgb(43, 43, 43) !important;
overflow: hidden !important;
-moz-transition-property: all !important;
-moz-transition-duration: 1.3s !important;
-webkit-transition-property: all !important;
-webkit-transition-duration: 1.3s !important;
  z-index: 999 !important;
}

#watch7-content:hover
{
background: white !important;
right: 0px !important;
max-width: 650px !important;
width: 99% !important;
padding-right: 0px !important;
overflow: scroll !important;
overflow-x: hidden !important;
height: 100% !important;
}

#masthead-search
{
position: fixed !important;
left: 8px !important;
top: -53px !important;
min-width: 210px !important;
max-width: 210px !important;
height: 27px !important;
}

#yt-masthead-container
{
background: none !important;
border: none!important;
}

#yt-masthead-user
{
position: absolute !important;
left: 88px !important;
top: -54px !important;
}

#masthead-expanded
{
position: absolute !important;
left: 283px !important;
top: -13px !important;
width: 0% !important;
}

  .html5-endscreen
  {
pointer-events:all !important;


}
#footer-container, span.stat.attribution,
#masthead-upload-button-group, #yt-masthead-user-displayname,
#logo-container, #watch7-headline, #guide, 
#masthead-expanded-menus-container, #masthead-appbar-container, #appbar-guide-button, 
#search-btn, .watch-sidebar-section, #upload-btn
{
  
display: none !important;
  
}
#body-cotainer, #masthead-positioner-height-offset, #yt-masthead-container,   watch clearfix watch-non-stage-mode { position: fixed !important;margin-left:-3383px !important; }
  #body { 
left: 0px !important;
  right: 0px !important;
padding-left: 0px !important;
  padding-right: 0px !important;

  }
  
  #ytp-upnext #ytp-suggestion-set
  { 
left: 0px !important;
  right: 0px !important;
padding-left: 0px !important;
  padding-right: 0px !important;
  z-index: 1000 !important;
  }
}
