css:
@-moz-document domain("endoftheinter.net") {


/* SITEWIDE */
	
	img[src="//static.endoftheinter.net/bhm.png"], img[src="//static.endoftheinter.net/pascal.png"] {display: none !important;}
 
body, body.regular, .body {
  background: #F9F9F9 url(http://i.imgur.com/anSVcTO.png) !important;
  margin: auto !important;
  color: #777 !important;
}
.body {
  width: calc(100% - 20px) !important;
	background-image: url(http://i.imgur.com/KgOkmhG.png) !important;
}

	body, table.classic tr td, table.grid tr td {
	  font-family: Segoe UI, sans-serif !important;
	  font-size: 13px !important;
	}
	
	/* bold semibolds; double bold superbolds */
	b {font-weight: 600 !important;}
	b b {font-weight: 700 !important;}
	
	img {border-radius: 2px !important;}

	/* Hide topbar */
	.menubar {display:none!important}
	
	/* sitewide bars */
	.userbar {
		opacity: 0 !important;
		font-size: unset !important;
		font-weight: unset !important;
	}
	.userbar a {
		font-weight: 600 !important;
	}
	.userbar:hover {
		opacity: 1 !important;
	}
  
	.userbar, .infobar {
	text-align: left !important;
	background: none !important;
	padding: 0 !important;
	}
		.userbar a:first-child {padding-right: 12px !important;}
		.userbar a:not(:first-child), .infobar a {
		padding: 0 5px !important;
		}		
		
		/* infobars: u0_2, 3, 4  - interferes w/ colors.php selection??? */
		.infobar {
			color: #999 !important;
			font-size: unset !important;
			font-weight: 400 !important;
		 }
		 .infobar a {
			 color: #777 !important;
			 font-weight: 600 !important;
		 }
		 
		 
		 
	
	
	/* Board titles, topic titles */
	h1 {
	font-size: unset !important;
	text-align: left !important;
	margin: 0 auto 3px 0 !important; /* adjusts for hiding top menubar */
	font-style: normal !important;
	font-weight: 500 !important;
	opacity: 0.6 !important;
	}
	
	/* random red text */
		em {
		color: #660000 !important;
		}
		
		/* archived warning */
		.body > h2:nth-child(6) > em:nth-child(1) {
		font-size: 16px !important;
		font-family: Helvetica Neue, sans-serif !important;
		color: #8C0000  !important;
	   font-style: italic  !important;
		}
	
	
	 /* sqlly stuff */
	 small {
	 display: block !important;
	 text-align: center !important;
	 clear: both !important;
	 opacity: 0.5 !important;
	 margin: auto auto 0 auto !important;
	 }
	 
	

	
}



/* MAIN page */
@-moz-document 
url(http://endoftheinter.net/main.php), url(https://endoftheinter.net/main.php) {




/* dot */
.body > div:nth-child(2) {
display:none !important;
}

h1, .infobar {
display: none !important;
}

  h2 {
    font: unset !important; 
    font-size: unset !important;
  }


/* big table */
.body > table:nth-child(4) {
width: 100% !important;
margin: auto !important;
}
/* center */
body.regular {
display: inline !important;
width: auto !important;
margin: auto !important;
text-align: center !important;
}
/* realign inner contents left */
.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) td {
text-align: left !important;
}


	/* CELL all the topic of the moment stuff is in */
	.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
	display:table !important;
	width: auto !important;
	margin: 0 auto 0 auto !important;
	padding: 0 0px 0 0 !important;
	}

		/* cell the topic list is in */
		.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
		padding: 0 !important;
		}

			body table.grid {
			padding: 0 !important;
			margin: 0 28px 20px 0 !important;
			border-radius: 2px !important;
			}

	/* POLL cell */
	.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) {
	width: 25% !important;
	margin: 0 !important;
	padding: 0 !important;
	}
		
		/* poll  and active tags boxes*/
		div.poll {
		margin: 0 0 20px 0 !important;
		}
		
		/* poll */
		div.poll,
		.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(4)		{
		border-radius: 2px !important;
		padding: 6px 10px !important;
		}
		
		/* vote button */
		div.poll form input[name="go"] {
		display:block !important;
		margin: -1em auto 0 auto !important;
		}
		/* view results / add poll / view old polls */
		div.poll form a {
		font-size: 11px !important;
		text-align: center !important;
		}


		/* active tags */
			/* box */
			.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(4) {
			color: rgba(0,0,0,0) !important;
			padding: 8px 14px !important;
			text-align: center !important;
			}
			/* tags themselves */
			.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(4) > a {
			display: inline-block !important;
				/*font-family: Droid Serif, Georgia !important; */
				font-family: Verdana !important;
				font-size: 11px !important;
				font-weight: 500 !important;
				text-align: left !important;
				padding: 2px 4px !important;
				background: rgba(255,255,255,0.2) !important;
				border: 1px solid rgba(0,0,0,0.2) !important;
				border-radius: 3px !important;
				color: rgba(0,0,0,1) !important;
				transition: 300ms !important;
				margin: 2px -4px !important;
			}
			.body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(4) > a:hover {
			background: rgba(255,255,255,0.6) !important;
			transition: 200ms !important;
			}
		
		
		
	





}


/* BOARDS */
@-moz-document 
url-prefix(http://boards.endoftheinter.net/topics), url-prefix(https://boards.endoftheinter.net/topics),  
url-prefix(http://boards.endoftheinter.net/history.php), url-prefix(https://boards.endoftheinter.net/history.php),
url-prefix(http://archives.endoftheinter.net/topics), url-prefix(https://archives.endoftheinter.net/topics),  
url-prefix(http://endoftheinter.net/main.php), url-prefix(https://endoftheinter.net/main.php), 
url-prefix(http://endoftheinter.net/priv.php), url-prefix(https://endoftheinter.net/priv.php),
url-prefix(http://endoftheinter.net/inbox), url-prefix(https://endoftheinter.net/inbox) {

/* TOPIC LIST */

	h1 {
	display:none !important;
	}
	
	/* top bars */
	.userbar {
	margin: 0 0 8px 0 !important;
	}
	.infobar {
	margin: 0 0 8px 0 !important;
	}
	
	/* Table */
	 table.grid {
	 border-radius: 2px !important;
	 width: auto !important; 
	 margin-right: auto !important; 
	 background: none !important;
	 }

	 
	 
	/* cells */
	 table.grid tr td {padding: 3px 6px 3px 6px !important;}
	 
	/* hide last post column in topic list */
	table.grid tbody th:last-child, table.grid tbody td:last-child {display:none !important;}
	
	/* Topic title column (first cell)  */
	table.grid tbody td:first-child {max-width: 50% !important;
	}
	
	/* Msgs column, Created By columns - prevent line breaks */
	    table.grid tbody td:nth-child(2), table.grid tbody td:nth-child(3) {white-space: nowrap !important;}
		
/* Topic list rows */
    
	/* hide column headers */
	table.grid th {display:none !important;}
	
	/* remove borders */
    table.grid tr td {
    background: none !important;
	border: none !important;
	}
	
	/* hover chaser */ /* fixme; messes with FOXlinks highlighting 
	table.grid tr:hover td {
	color: #000; 
	background-color: #E6EFF2 !important; 
	transition: 0ms !important;
	}
    table.grid tr:not(:hover) td {
	transition: 200ms !important;
	}
	*/
	
	 /* x link*/
	a[onclick^="return"] {opacity: 0.2 !important;}
	a[onclick^="return"]:hover {opacity: 1 !important; transition: 500ms !important;}

			/* make even so the homepage doesn't get weird 
		tbody tr:nth-child(even) td {background-image: url(http://i.imgur.com/dLlHlaI.png) !important;}
	*/
	
	
    


/* TOPIC LIST TAGS */

/*.put tags under topic title */
.fl, .fr {float:none !important;} 
/*.fl:after {content: " " !important;} */

/* tags */
.fr {
margin: 0 0 0 1em !important; 
font-size: 10.5px !important;

display: none !important;
}

	/* add bullets between tags 
    .fr a[href^="/topics/"]:not(:nth-last-child(2)):after { 
	content:"•" !important; 
	margin: 0 4px 0 6px !important; 
	color: rgba(0,0,0,0.3) !important;
	}
	*/
    .fr a {
    opacity: 0.5 !important; 
	margin: 0 1em 0 0 !important;
    /* font-size: 10.5px !important;  */
    /* text-transform: uppercase;  */
    /* font-variant: small-caps; */
    }
	
	/* red NWS/NLS */
    .fr a[href="/topics/NWS"], .fr a[href="/topics/NWS"]:visited {color: maroon !important; opacity: 0.7 !important;}
    .fr a[href="/topics/NLS"], .fr a[href="/topics/NLS"]:visited {color: maroon !important; opacity: 0.7 !important;}
/*    .fr a[href="/topics/Spoilers"], .fr a[href="/topics/Spoilers"]:visited {color: orange !important;} */

    /* .fr a:visited {color:#444 !important;} use colors.php */
    /*.fr a:hover {color:#36c !important;} use colors.php */

	/* page jump (#) */
    a[title="Page Jump"] {display:none!important;}
	/* go to last page (>) */
    a[title="Jump to last page in topic"] {float: right !important; font-size: 0px !important; color: transparent !important;}
	a[title="Jump to last page in topic"]:after {content: "►" !important; color: rgba(0,0,0,.25) !important; font-weight: 800 !important; font-size: 9px !important; font-family: Arial !important; display: inline-block !important; padding: 0px 4px !important; }
    a[title="Jump to last page in topic"]:hover:after {color: rgba(0,0,0,1) !important;}



}



	/* Message History-specific stuff */
	@-moz-document 
	url-prefix(http://boards.endoftheinter.net/history.php), url-prefix(https://boards.endoftheinter.net/history.php) {
		
		 /* top pages: Page 1 of 18 | Next Page | Last Page 
		 .infobar {
		 text-align: center !important;
		 } */
		 
    /* Hides board name column 
		table.grid th:first-child, table.grid td:first-child, */  
    /* Hides post date/time  column*/
		table.grid th:nth-child(4), table.grid td:nth-child(4) 
		{display:none !important;}	

		 
	}


/* IN-TOPIC VIEW */
@-moz-document 
url-prefix(http://boards.endoftheinter.net/showmessages.php), url-prefix(https://boards.endoftheinter.net/showmessages.php),
url-prefix(http://archives.endoftheinter.net/showmessages.php), url-prefix(https://archives.endoftheinter.net/showmessages.php),
url-prefix(http://endoftheinter.net/inboxthread.php), url-prefix(https://endoftheinter.net/inboxthread.php)
 {
	
	
	/* in-Topic title */
	h1 {
	text-align: left !important;
	margin-left: 30px !important;
	}
	
	/* in-topic tags */
		h2 { 
		font-family: Verdana !important;
		font-size: 9px !important;
		font-weight: 500 !important;
		text-align: left !important;
		margin: 6px 0 20px 30px !important;
		}

		h2 div > a:first-child {
		margin-right: 3px !important;
		}
		/* space around every tag but the first */
		h2 div > a:not(:first-child) { 
		margin: 0 3px !important;
		}
		h2 a {
			color: #999 !important;
			padding: 2px 4px !important;
      line-height: 2.2; /* prevents multiple lines from overlapping */
			background: rgba(255,255,255,0.2) !important;
			border: 1px solid rgba(0,0,0,0.15) !important;
			border-radius: 3px !important;
			opacity: 1 !important;
			transition: 100ms !important;
		}
		h2 a:hover {
		background: rgba(255,255,255,0.5) !important;
			border-color: rgba(0,0,0,0.25) !important;
			transition: 100ms !important;
		}
	/* add brackets 
		h2 a:before {
		content: "[" !important;
		color: #999 !important;
		margin-right: 2px !important;
		} 
		h2 a:after {
		content: "]" !important;
		color: #999 !important;
		margin-left: 2px !important;
		} 
		*/
			/* subtags */
			h2 span {
			font-size: 10px !important;
			opacity: 0.5 !important;
			vertical-align: middle !important;
			padding: 0 3px 0 6px !important;
			}
			h2 span a{
			padding: 0 !important; /* unstyle override */
			margin: 0 !important;
			border: none !important;
			background: none !important;
			}
			h2 span {
			margin: 0 0 0 -8px !important;
			}

		
	/* Top bars - userbar and page x */
	.userbar, #u0_2.infobar {
	background: none !important;
	}
	.userbar {float: left !important; margin: 0 0 0 30px !important;}
	.infobar {float: right !important; margin: 0 30px 0 0 !important;}
	

	 
	 
	 /* Outer container */
	 #u0_1 {
	 padding: 0px  30px !important; 
	 background: none !important;
	 clear: both !important;
	 }
	 
	 
	/* POSTS */
	 
	 .message {background: none !important;}
	 
	.message-container {
	/* box-shadow: 0 0 9px rgba(0,0,0,0.5) !important; */
	margin: 0 !important;
		padding: 0 0 22px 0 !important;
	}


		/* message header */
		.message-top {
		font-size: 12px !important;
		border-radius: 1px !important;
		background: none !important;
		opacity: 0 !important;
		}
	 .message-top, .message-top a {
		 color: #999 !important;
	 }
		.message-top:hover {opacity: 1 !important;}
		
			/* hide From: fixme - interferes with quoting via selection??  */
			.message-top b:first-child {display:none !important;}  
			
			/* username */
			.message-top a:nth-child(2), 
			.message-top span[style="color:green;font-weight:bold"] + a /*fixes moneybags */ {
			font-size: 13px !important; 
			font-weight:600 !important;
			margin-right: 2px !important;
			} 
			/*.message-top, .message-top a {color: rgba(0,0,0,0.75) !important;} interferes with FOXlinks highlighting */
			
			/* userpics on the left */
			.message-body {direction: rtl !important;}
			.message-body > * {direction: ltr !important;}
			
			/* post */
			.message-body .message {
			padding:5px!important;
			}

				/* QUOTES */
				.quoted-message {
				word-spacing: 0 !important; /* i forget what this does fixme */
				overflow: hidden !important; /* prevents userpic interference */
				border: 1px solid #DDD!important;  
				margin: 3px 16px 0px 16px !important;
				padding: 4px 6px 6px 6px !important;
				color: #333 !important;
				border-radius: 2px !important;
				}
/*				indent quotes-within-quotes
				.quoted-message .quoted-message {
				margin-left: 8px !important;
				}*/
				/* remove extra space at bottom of quote  - fixme
				 .message-container .message-body tbody tr td.message .quoted-message:first-child { 
				margin-bottom: 0px !important;
				}*/
				.quoted-message .message-top {
				padding: 0 2px 2px !important; 
				margin: 0 !important;
				background: rgba(0,0,0,0)!important;
				font-size: 10.5px !important; 
				font-style: italic !important; 
				font-weight: 200 !important;
				}
				
				/* all quoted messages */
				.quoted-message .message-top a:nth-child(2) {font-size: 11px !important; font-weight: 400 !important;}
				.quoted-message .message {margin: 0 !important; padding: 0 !important; background: rgba(0,0,0,0) !important;}
				td.message > .quoted-message .message-top, td.message > .quoted-message .message-top a {
				color: #999 !important;
				}
				td.message > .quoted-message .message-top a:first-child {
				font-weight: 600 !important;
				}
				/* Goto link 
				.quoted-message a[href="#jumpto"]:after {content: " →" !important;}
				*/
				
					/* hierarchy colors */  /* messes w/ FOXlinks highlighting */
					td.message > .quoted-message
					{
					color: #888 !important;
					background: rgb(238,238,238) !important;
					} 
					td.message > .quoted-message .quoted-message
					{
					color: #999 !important;
					background: rgb(234,234,234) !important;
					} 
						td.message > .quoted-message .quoted-message .quoted-message,
						td.message > .quoted-message .quoted-message .quoted-message a[href^="/message.php"]
						{
							color: #AAA !important;
							background: rgb(230,230,230) !important;
							}
	 								.quoted-message .message .quoted-message .message .quoted-message .message .quoted-message {
										color: #BBB !important;
										background: rgb(226,226,226) !important;
									}

					td.message > .quoted-message {border: 1px solid rgb(221,221,221) !important;} /* #DDD = 221 */
					td.message > .quoted-message .quoted-message {border: 1px solid rgb(216,216,216) !important;}
					td.message > .quoted-message .quoted-message .quoted-message {border: 1px solid rgb(210,210,210) !important;}
					
							

				/* avatar */
				.userpic-holder, .userpic, .photo-album-image, td.userpic {
					display: none !important;
				}
	 
	 
	.message-container {
		border-bottom: 1px solid #DDD !important;
	}
	.message-container:last-child {
		padding-bottom: 30px !important; 
	/*	border-bottom: 1px solid #DDD !important; */
	}
	 
	/* big next page button */	
	a#nextpage {
	padding: 30px !important;
	background: #EEE !important;
	border: 1px solid #DDD !important;
	border-radius: 4px !important;
	}
	a#nextpage:hover {
	background: #E9E9E9 !important;
	}



	 /* bottom infobars */
		 #u0_3.infobar, #u0_4.infobar {
/*		 display: block !important; */
		 }
		 
		 /* There are currently x people reading this topic - fixme (colors.php) */
