		 margin: 10px 10px 10px 40px !important;
		 float: left !important;
		 text-align: left !important;
		 min-width: 30% !important;
		 max-width: 350px !important;
		 margin-bottom: 350px !important; /* padding for quickpost */
		 }
		 
		 /* bottom page number selectors */
		 #u0_3.infobar {
		 margin: 10px 40px 10px 10px !important;
		 max-width: 50% !important;
		 color: rgba(0,0,0,0.3) !important;
		 text-align: right !important;
		 }
/*	more space between pages
	 #u0_3.infobar a:not(:last-child) {
		 margin: 0 -2px !important;
		 }*/
		 #u0_3.infobar a { /* make it look like the in-topic tags */
		   
		   color: rgba(0,0,0,0.6) !important;
		   line-height: 2em !important;
			font-size: 11.5px !important;
			padding: 1px 5px !important;
			background: rgba(255,255,255,0.2) !important;
			border: 1px solid rgba(0,0,0,0.2) !important;
			border-radius: 3px !important;
		 }
		#u0_3.infobar a:hover {
			background: rgba(255,255,255,0.04) !important;
			}
		 

	 
	 
	 
	 /* quickpost button */
	.quickpost-nub {display:none !important;}

	 	

}

/* Create New Topic page */
@-moz-document url-prefix(http://boards.endoftheinter.net/postmsg.php), url-prefix(http://boards.endoftheinter.net/postmsg.php) {


/* form 
.body > form {
display:table !important;
margin: auto !important;
}
*/

/* replace "post message" with "create new topic" */
.body > h1:nth-child(3) {
display:none !important;
}
.body > form:nth-child(4) > b:nth-child(4) {
	display: block !important;
	margin: auto !important; /* adjusts for hiding top menubar */
	font-size: 24px !important;
	font-family: Helvetica Neue, sans-serif !important;
	font-style: normal !important;
	font-weight: 600 !important;
	opacity: 0.6 !important;
}

.body > form:nth-child(4) > br:nth-child(2), .body > form:nth-child(4) > br:nth-child(3),
#token_desc > br:nth-child(1) {
display:none !important;
}


/* hide red text */
.body > form:nth-child(4) > span:nth-child(1), /* the rules */
.body > form:nth-child(4) > em:nth-child(12) /* against the rules to add tags */ {
display:none !important;
} 


/* post button on left */
#buttons {
display: -moz-box !important;
}
#buttons > input:nth-child(3) {
-moz-box-ordinal-group: 0 !important;
}

}

@-moz-document domain("endoftheinter.net") {
	
	/* links */ 
	a {color: #555 !important;}
	a:visited {color: #777 !important;}
	a:link,a:visited, a:hover, a:active {text-decoration:none !important; outline: none !important;}

	a:not(:hover) {} 
	a:hover {color:#36c!important;} 
	a:active {color: #5AB0DB !important;}
	/* colors.php doesn't have a:hover for topic list links */
}
