css:
@-moz-document domain("tareas.opensoft.cl") {
textarea{
	resize: vertical;
    max-height: 50em;
}

a:hover{
	text-decoration: none;
    color: black;
}

#short_desc{
	border: none !important;
    background: none;
    font-weight: bold;
    color: #006699;
    font-size: 1.45em;
    box-shadow: none;
    cursor: pointer;
}

#short_desc:hover{
	color: #0088bb;
}

#short_desc:focus{
	border: 1px solid #444;
    background: white;
    cursor: text;
}

*{
	box-sizing: border-box;
    position: relative;
    font-family: "Open Sans", Helvetica, Arial, sans-serif !important;
}

br + br +table[border="0"][cellpadding="3"][cellspacing="0"]{
	width: 100%;
}

#edit_bug_menu{
	width: 97%;
    padding: 1em;
    border: 1px solid #ccc;
    border-radius: 3px;
}

#edit_bug_menu li:after{
	content: "";
    display: block;
    width: 100%;
    float: none;
    clear: both;
}

#edit_bug_menu li img{
	margin-right: 0.5em;
}

.frm{
    padding: 1em;
    border: 1px solid #ccc;
    border-radius: 3px;
}

#edit_bug_menu a{
	display: block;
}

#edit_bug_menu a:hover{
	text-decoration: none;
    color: black;
}

#attachment:after{
	content:"";
    display: block;
    width: 100%;
    clear: both;
    float: none;
    font-size: 0;
}

#attachment img{
	float: left;
    width: 5%;
}

#attachment a{
	float: right;
    width: 93.5%;
}

input#short_desc{
	width: 98%;
    padding: 0.2em;
}

#bugform_div form div + script + div + table[border="0"][cellpadding="3"][cellspacing="0"]{
	width: 100%;
}

#bugform_div form div + script + div + table[border="0"][cellpadding="3"][cellspacing="0"] td:first-child{
	width: 100px;
}

#bugform_div form div + script + div + table[border="0"][cellpadding="3"][cellspacing="0"] td:last-child{
	width: 90%;
    width:calc(100% - 100px)
}

input, select{
    padding: 0.3em;
    border-radius: 3px;
    box-shadow: inset 0 0 5px rgba(0,0,0,0.05);
    color: #444;
}

input:focus, select:focus{
	box-shadow: 0 0 5px rgba(0,0,255,0.2);
}

tr[id*=row] td{
	padding: 0.2em 0.2em;
}


tr[id*=row] td:first-child{
	width: 180px;
}

tr[id*=row] td:first-child *{
    font-size: 14px !important;
}

tr[id*=row] td:last-child{
	width: calc(100% - 180px);
}

tr[id*=row] td:last-child select, tr[id*=row] td:last-child input{
	width: 80% !important;
}

.cke_editor tr[role=presentation] *:not(*[class*=icon]){
	background: #e5e5e5 !important;
    border: none !important;
    cursor: pointer;
}

tr[role=presentation] td#cke_top_comment a.cke_toolbox_collapser[title="Contraer barra de herramientas"]{
    background-image: url(http://tareas.opensoft.cl/ckeditor/skins/office2003/images/sprites.png) !important;
    background-position: 3px -1026px !important;
  	background-repeat: no-repeat !important;
}

.cke_editor tr[role=presentation] td#cke_contents_comment iframe[title="Editor de texto, comment, pulse ALT 0 para ayuda."]{
	border: 1px solid #ddd !important;
}

.cke_editor tr[role=presentation] span#cke_29_text, .cke_editor tr[role=presentation] span#cke_30_text{
	overflow: visible;
    border: 1px solid #ddd !important;
    height: 24px;
    background: white !important;
    color: #333;
    width: 5em;
}


span#cke_comment{
	display: block;
    padding: 1em;
}

.cmt_text{
    display: block;
    padding-top: 1em;
    padding-bottom: 1em;
	font-family: "Open Sans", Helvetica, Arial, sans-serif;
}

.cmt_text p{
	margin: 0.1em 0;
}

.cmt, #posts_table{
	background: #eee;
    border: none !important;
}

.cmt{
	padding: 1em;
}

tr:nth-of-type(even) .cmt{
	background: #f3f3f3;
    width: 100%;
}

.buglist_popup, #posts{
	border: none;
}

td.cmt > table:first-child{
	border-collapse: collapse;
    width: calc(100% + 1.4em);
    margin-left: -0.75em;
}

td.cmt > table:first-child > tbody > tr:first-child > td{
    background: white;
    padding: 0.5em;
    border: none;
    position: static;
}

td.cmt > table:first-child > tbody > tr:first-child > td:last-child{
	width: 225px;
}

td.cmt > table:first-child > tbody > tr:first-child > td .pst{
	font-style: italic;
    color: #444 !important;
}

td a.warn{
	float: left;
}

.private{
    background-color: #dd2222;
    color: white;
    width: 120px;
    letter-spacing: 0;
    font-family: "Open Sans", Helvetica, Arial, sans-serif !important;
    text-align: center;
    padding: 0.1em 0.5em;
    font-size: 12px;
    font-weight: normal;
    position: absolute;
    top: 0.5em;
    right: 0;
    border-radius: 3px;
    box-shadow: 1px 1px 3px black;
    z-index: 10;
}

.private:before{
	content:"";
    display: block;
    width: 2em;
    height: 90%;
    position: absolute;
    top:0;
    right: 99%;
    border-right:1em solid #dd2222;
    border-top: 0.83em solid transparent;
    border-bottom: 0.83em solid transparent;
    box-sizing: border-box;
    z-index: 1;
}

.private:after{
	content:"";
    display: block;
    width: 2.1em;
    height: 90%;
    position: absolute;
    top:0.05em;
    right: 99%;
    border-right:1em solid black;
    border-top: 0.83em solid transparent;
    border-bottom: 0.83em solid transparent;
    box-sizing: border-box;
    z-index: 0;
    opacity: 0.5;
}

}
