/* ==UserCSS==
@name osu!dark transparent
@url 
@idUrl 
@md5Url 
@md5 
@updateUrl 
@enabled true
==/UserCSS== */
@-moz-document domain("osu.ppy.sh"){
body, textarea, input, textarea:focus, input:focus {
	color: #ddd;
}

textarea {
	background-color: rgba(0,0,0,0.3);
}

a {
	color: #FFFFFF!important;
	text-shadow: #FFFFFF 0px 0px 1px;
}

a:hover {
	color: #FFFFFF;
	text-shadow: #FFFFFF 0px 0px 5px;
}

a:visited {
	color: #90ACE7;
}

input, select {
	border: 1px solid #15BFDF;
	background-color: rgba(0,0,0,0.3);
	color: #FFFFFF;
}

input[type="submit"], input[type="button"] {
	background-color: rgba(0,0,0,0.3);
	color: #FFFFFF;
}

input[type="submit"]:hover, input[type="button"]:hover {
	background-color: rgba(224,240,48,0.7);
	color: #FFFFFF;
}

h1,h2,h3,h4,h5 {
	color: #FFFFFF!important;
	text-shadow: #FFFFFF 0px 0px 10px!important;
}

h2, h2 a {
	color: #FFFFFF;
	text-shadow: #FFFFFF 0px 0px 10px;
}

.sfHover {
	background-color: #FFFFFF!important;
}

.sf-menu, .sf-menu a, .sf-menu li {
	background-color: rgba(0,0,0,0.3)!important;
} #FFFFFF

a.hovering {
	color: #FFFFFF!important;
	background-color: rgba(224,240,48,0.7)!important;
}

div#coolheader, .mainbody2, .footer, .final_footer {
	background-image: none!important;
}

.footer {
	background-color: rgba(0,0,0,0.5);
	border-bottom-left-radius: 10px;
	border-bottom-right-radius: 10px;
}

#playstats {
	margin-right: 8px;
	padding-left: 5px;
	background-color: rgba(0,0,0,0.5);
}

.news-heading {
	border-bottom: 1px solid #0ef;
}

.news-text {
	background-color: rgba(0,0,0,0.3);
	/*border-top: 1px solid #0ef;*/
}

.content-infoline {
	background-color: rgba(0,0,0,0.7);
	border-top-left-radius: 10px;
	border-top-right-radius: 10px;
	height: 24px;
}

.content-with-bg {
	background-image: none!important;
	background-color: rgba(0,0,0,0.5);
	margin-top: 0px;
}

.row1 {
	background-color: rgba(0,0,0,0.3);
}

.row2 {
	background-color: rgba(0,0,0,0.2);
}

.row3 {
	color: #aaa;
}

td.chattime {
	color: #aaa;
}

/** userpage **/
.userpage, .posttext, .quickreply, .changelog-text {
	border: 1px solid #0bd;
	background-color: rgba(0,0,0,0.3)!important;
}

.userpage-expand {
	background-color: rgba(0,0,0,0.5);
}

.userpage-expand:hover, .spoiler:hover .spoiler_head {
	background-color: rgba(224,240,48,0.5);
}

.frozen-header, .playstyle-container {
	background-color: rgba(0,0,0,0.3);
	border-radius: 5px;
}

.postedit {
	background-image: url("http://i.imgur.com/TfTdoPd.png")!important;
}

.sectionHeading, .sectionHeading:hover, #tablist a.active  {
	background-image: url("http://freetotalsoftware.com/wp-content/uploads/2015/02/black-wallpapers-black-backgrounds-desktop-abstract-picture-black-wallpaper.jpg")!important;
	background-color: rgba(0,0,0,0.5);
}
	
.profileSupporter, .profileSupporter:hover {
	background-image: url("http://a.pomf.se/sdkgrm.png")!important;
}

.sectionHeading, .sectionHeading:hover {
	color: #ce3;
	text-shadow: 0px 0px 3px #0bd;
}

svg text {
	fill: #ddd;
}

.profile-tabs td {
	background-color: rgba(0,0,0,0.3);
	border-color: #0bd;
}

.profile-tabs td:hover {
	background-color: rgba(0,176,208,0.3);
}

.profile-username {
	color: #ddd;
	text-shadow: #333 0px 0px 10px;
}

.avatar-holder {
	background-color: transparent;
}

.btn {
	background-color: transparent!important;
	border: 2px solid;
}

.profileGameModes a {
	background-color: rgba(0,0,0,0.3);
	border-color: #ce3;
}

.profileGameModes .active {
	background-color: rgba(224,240,48,0.7);
	color: rgba(0,0,0,0.7)!important;
}

.spoiler_head {
	border-color: #ce3;
}

.sectionContents, .spoiler_body {
	background-color: rgba(0,0,0,0.3);
}

.profileStatLine, .profileStatHeader {
	background-color: rgba(0,0,0,0.3);
}

.profileStatHeader {
	color: #7c7;
}

.profileBeatmapHeader:hover {
	background-color: rgba(64,168,255,0.2);
	color: #ddd;
}

.levelPercent {
	color: #555;
}

.prof-beatmap {
	background-color: rgba(0,0,0,0.3);
	border: 1px solid #0ef;
	border-radius: 3px;
}

.prof-beatmap .pp-display {
	color: #ce3;
	text-shadow: #8e3 0px 0px 10px!important;
}

.prof-beatmap .pp-display-weight {
	color: #aaa;
}
/** settings **/
.mutual {
	background-color: rgba(224,240,48,0.7);
	border-color: #ce3;
}

.mutual a {
	color: rgba(0,0,0,0.7)!important;
}

.row1:hover, .row2:hover, tr.row1p:hover, tr.row2p:hover, tr.row3p:hover, tr.row4p:hover, .tablebg .row1:hover {
	background-color: rgba(0,0,0,0.4)!important;
}

.forum_table tr:hover {
	color: #ddd;
}

.postcount {
	color: #aaa;
}

.top-maps-beatmap .header {
	color: #ec3!important;
}

.btn.red {
	background-image: none;
	color: red!important;
	text-shadow: 0px 0px 10px red;
}

.stream {
	border-color: #ce3;
}

.stream:hover {
	background-color:  rgba(0,0,0,0.3);
}

.stream>.title {
	color: #ddd;
}

/** beatmap **/
#tablist, #tablist a, #songinfo td.colour {
	background-color: rgba(0,0,0,0.3);
}

#tablist a.active {
	background-color: rgba(0,0,0,0.3)!important;
}
	
.beatmapTab active {
	background-image: none!important;
}
	
.beatmapTab {
	background-image: none!important;
}

#songinfo {
	background-color: transparent;
	color: #ddd;
}

#songinfo td {
	background-color: rgba(0,0,0,0.5);
}

#songinfo td.colour {
	color: #aaa;
}

.row1p {
	background-color: rgba(0,0,0,0.3);
}

.row2p {
	background-color: rgba(0,0,0,0.5);
}

.row3p {
	background-color: rgba(200,240,100,0.5);
}

.row4p {
	background-color: rgba(240,200,100,0.5);
}

.row3p a, .row4p a {
	color: #444!important;
	text-shadow: 0px 0px 10px #333!important;
}

.row3p:hover a, .row4p:hover a {
	color: #15DFDF!important;
	text-shadow: #10CFEF 0px 0px 10px!important;
}

/** forum **/
.tablebgsideNoSig, .pack-msg {
	background-color: rgba(0,0,0,0.3);
}	

.postLeft, .postTime, .postbody {
	background-color: transparent;
}

.postauthor {
	color: #ce3!important;
	text-shadow: #ce3 0px 0px 10px;
}

.codecontent {
	color: #ddd;
}

.osu-modheader {
	color: #ce3;
}
	
.mat,.supporter{
	background:url("http://a.pomf.se/sdkgrm.png");
	background-repeat:no-repeat;
}

.osu-timecode {
	background-color: rgba(0,0,0,0.3);
	color: #d57;
}

.osu-timecode:hover {
	background-color: rgba(0,0,0,0.1);
}

.postKudosu, .postButtons, .codecontent {
	background-color: rgba(0,0,0,0.3);
}

.btn {
	background-image: none;
}

.quotecontent {
	background-color: rgba(0,0,0,0.3);
	border-width: 0px 0px 0px 5px;
	border-color: #ce3;
}

.post {
	background-color: transparent;
}

.tablebgside {
	background-color: transparent;
}

.bar {
	background-color: rgba(200,240,100,0.5);
}

/** search **/
#searchArea a:hover, #searchArea a.selected {
	background-color: rgba(0,0,0,0.3);
	border: 1px solid #ce3;
}

#searchArea dl {
	border-bottom: 1px dotted #ce3;
}

.beatmapListing th {
	background-color: rgba(0,0,0,0.3);
	border-bottom: 1px solid #ce3;
}
.beatmapListing th:hover {
	background-color: #ce3;
	border-bottom: 1px solid #ce3;
	color: #444;
}

.beatmap, .bmlistt {
	height: 64px;
}

.beatmap .artist {
	color: #aaa;
}

.beatmap, .changelog_area, .beatmap .tags a {
	background-color: rgba(0,0,0,0.3);
	border-color: #ce3;
}

.bmlist-options {
	background-color: rgba(0,0,0,0.3);
}

.beatmap:hover, .beatmap .tags a:hover {
	background-color: rgba(0,0,0,0.5);
	border-color: #ce3;
}

.beatmap .rating {
	border-color: #555;
}

.posthilit {
	color: #ce3;
}

/** changelog **/
.cat h4 {
	background-image: none;
	background-color: rgba(224,240,48,0.7);
	color: #444!important;
}

.blog-post {
	background-color: transparent;
}

/** support **/
.dialog-area, .peppynote {
	background-color: rgba(0,0,0,0.3)!important;
}

.peppynote {
	border-color: #ce3!important;
}

.reasonpanels>li {
	background-color: rgba(224,240,48,0.5)!important;
	color: #444!important;
}

.slider, .ui-progressbar {
	border-width: 1px;
	border-color: #0bd;
	background-color: rgba(0,0,0,0.3);
	background-image: none;
}

.ui-slider-range, .ui-progressbar-value {
	background-color: rgba(224,240,48,0.5);
	background-image: none;
}

}

@-moz-document regexp("http[s]?://osu\\.ppy\\.sh/wiki/.*"){
/* wiki */

body,p,li {
	color: #ddd!important;
}

div.mw-panel {
	background-color: rgba(0,0,0,0.5)!important;
	border-color: #ce3!important;
}

.toc {
	background-color: rgba(0,0,0,0.3)!important;
	box-shadow: 0px 0px 10px inset;
}


h1,h2,h3,h4,h5 {
	color: #ce3!important;
	text-shadow: #8e3 0px 0px 10px!important;
}

a {
	color: #0bd!important;
	text-shadow: #10CFEF 0px 0px 10px;
}

a:hover {
	color: #528E90;
}

a:visited {
	color: #528E90;
}

tr {
	color: #ddd!important;
	background-color: transparent!important
}
}

@-moz-document domain("osu.ppy.sh"){
.bodytopbg {
	background-image: url("http://i.imgur.com/TfTdoPd.png") !important;
	background-size: cover!important;
	background-position: 50%!important;
	background-repeat:no-repeat!important;
	background-attachment:fixed!important;
}
}

@-moz-document domain("osu.ppy.sh") {
    
    .bodytopbg, div.header.clickable-cell,
    .bmlist-options > a, .icon-comments
    {
        background: rgb(25, 25, 25);
    }
    
    .beatmapListing > tbody > tr > th,
    .bmlist-options,
    .content-infoline, #playstats,
    .historyp,
    .beatmapListing th,
    #tablist a,
    .tablebg.forum_table > tbody > tr > td.row1, .tablebg.forum_table > tbody > tr > td.row2,
    .frozen-header,
    div#mw-head-base, div#mw-page-base, .profileStatLine, .profileStatHeader
    {
        background: rgb(30, 30, 30);
    }
    
    .beatmap, .beatmap:hover, .tablebg .row1:hover, .tablebg .row2:hover, .profileGameModes a, .profile-tabs td
    {
        background: rgb(35, 35, 35);
        border: 1px solid rgb(70, 70, 70);
    }
    
    .content-with-bg, .footer, .pack-msg,
    #tablist A:hover,
    .sf-menu a:hover, .sf-menu a:focus, .sf-menu li.sfHover
    {
        background: rgb(40, 40, 40);
    }
    
    .dialog-area, .peppynote, .reasonpanels > li, .reasonpanels > li:hover > i,
    .changelog-text,
    .row1p,
    li.sfHover > ul > li > a,
    .login-dropdown,
    .blurb, .news-text, .row1,
     .postRight, .postbody, .postTime, .tablebgside > tbody > tr > td, .quotecontent, .sectionContents, .playstyle-container
    {background: rgb(60, 60, 60)}
    
    .beatmapListing tr.row1p:hover, .beatmapListing tr.row2p:hover 
    {
        background: rgb(80, 80, 80)
    }
    
    #toc, table.wikitable, .wikitable th
    {background-color: rgb(50, 50, 50); background: rgb(50, 50, 50)}
    
    .beatmap .tags a {background: rgb(60, 60, 60); color:black; border: rgb(70, 70, 70);}
    
    .row1p > td > a, .row2p > td > a, .row1p > td > a:hover, .row2p > td > a:hover
    {color: rgb(170, 170, 170);}
    
    sf-menu a{color:#77297D;}
    
    a, .sf-menu a, #general > font
    {color:rgb(168, 51, 138);}
    
    .blurb, .news-text, .main-box{border: 1px solid rgb(70,70,70)}

    .blog-post{background:none}
    
    .content-with-bg > h1, .content-with-bg > h2, .login-open-button, .login-open-button:hover, .new-user-button:hover,
    .ntitle,
    .row1, .row2,
    .forum_posts_table tr:hover, h1, .prof-beatmap .pp-display, h2, .profileGameModes a:hover
    {text-shadow: none}
    
    div#coolheader {display: none;}
}
