css:
@-moz-document domain("voat.co"), domain("www.voat.co") {
 
body {
  background-image: none !important;
  background-color: black !important;
  font-family: 'Ubuntu', 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
  color: #999 !important;
}
  
/* Unneccesary fonts */
.alert-h1 {
  font-family: 'Ubuntu', 'Helvetica Neue', Helvetica, Arial, sans-serif !important;
}
  
/* Unneccesary backgrounds */
#header, #header-container, #container, div.even, a.contribute, li.btn-whoaverse-paging, .comment, .whoaSubscriptionMenu li ul li, .userstats, .userstats-heading-2, .well {
  background-image: none !important;
  background-color: transparent !important;
}
  
/* Overly bright backgrounds */
#sr-header-area, div.side, .link .usertext .md, .logged-in, .comment blockquote, .whoaSubscriptionMenu, .spacer.spacersection, .sidecontentbox .content {
  background-image: none !important;
  background-color: #272727 !important;
}

/* Unneccesary borders */  
#header, #header-container, #container, #sr-header-area, .thumbnail, a.contribute, li.btn-whoaverse-paging, .logged-in, .link .usertext .md, .comment, .userstats, .userstats-heading-2, .well, .sidecontentbox .content, .spacer.spacersection {
  border: none !important;
  border-radius: 0 !important;
}
  
/* Unneccesary widths */
#header-banner, .commenttextarea {
  width: auto;
}
  
/* Unneccesary min and max widths */
#header-banner, #container, .link .score, .commenttextarea {
  min-width: 0 !important;
  max-width: 9999px !important;  
}
 
/* Unneccesary margin and padding */
body, #container, .content, div.link, .link .title, .link .flat-list, .side, .tagline, .link .midcol, .arrow-upvote, .arrow-upvoted, .arrow-downvote, .arrow-downvoted, .md p, .comment, .comment .comment, .comment.thread > .comment.child {
  margin: 0 !important;
  padding: 0 !important;  
  line-height: 1.15em !important;
}
  
/* Repair broken 100% widths that other fixes broke */
#submission-preview-area, .commenttextarea, .page-userprofile .content, div.horizontal-line {
  width: calc(100% - 320px) !important;
}
  
/* Add a min height to comments */
.commenttextarea {
  min-height: 7em !important;
}
  
/* Add some margin to entries */
.entry {
  margin-bottom: 0.5em;
}
  
/* Overzealous margin and padding */
.link, .link .midcol, div.entry {
  padding: .25em !important;  
}
.md p {
  padding: .1em !important;  
}
.comment .comment {
  margin-left: 2em !important;
}
  
/* Unneccesary entire elements */
.sr-bar .separator, .scorebar {
  display: none !important;
}
  
/* Simplify header colors and size */ 
.link .title, .alert-title, .alert-h1, .alert-h2, .userstats-heading-1, #sr-bar li.selected a
{
  color: #32A9D5 !important;
  font-size: 1.1em !important;
}
 
/* Color tweaks to top menu bar */
#sr-bar:hover {
  background-color: inherit !important;
}
#sr-bar li a:hover {
  text-decoration: none !important;
  color: #32A9D5 !important;
}
  
/* Color tweak to border between items */
.submission, div.comment div.entry
{
  border-bottom: 1px solid #272727 
}

  
/* Annoying how some subaverses are capitalized and some arent, isn't it?  */
#sr-bar li a {
  text-transform: capitalize !important
}
  
} /* Close domain tag */
