css:
@-moz-document domain("8ch.net") {
.desktop-style div.boardlist:not(.bottom) {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  margin-top: 0;
  z-index: 30;
  box-shadow: 0 1px 2px rgba(0, 0, 0, .15);
  border-bottom: 1px solid;
  background-color: #ffe;
}

.content_menu {
  background-color: #EA8;
  border: 1px solid #000;
  text-align: left;
  list-style-position: inside;
  padding: 15px 30px;
  text-align: left;
}
.button {
  padding: 13px 18px;
  border: 2px solid #000;
  font-size: 2.75em;
  color: #AF0A0F;
  text-align: center;
  display: inline-block;
  background: #EA8;
  margin-right: 5px;
  margin-top: 15px;
  text-decoration: none;
  font-style: normal;
}
ol {
  background: #EA8;
  list-style-type: decimal;
  -webkit-margin-before: 0em;
  -webkit-margin-after: 0em;
  -webkit-margin-start: 0px;
  -webkit-margin-end: 0px;
  -webkit-padding-start: 40px;
  -webkit-margin-color: #EA8;
}
table tbody td {
  margin: 0;
  padding: 0px 0px 0px 0px;
  text-align: left;
}
.timeline-header a.follow-button:link, .timeline-header a.follow-button:visited, .timeline-header a.follow-button:hover, .timeline-header a.follow-button:focus, .timeline-header a.follow-button:active, .thm-dark .timeline-header a.follow-button:link, .thm-dark .timeline-header a.follow-button:visited, .thm-dark .timeline-header a.follow-button:hover, .thm-dark .timeline-header a.follow-button:focus, .thm-dark .timeline-header a.follow-button:active {
  color: #800000;
  text-decoration: none;
}
.follow-button:link, .follow-button:visited {
  display: inline-block;
  padding: 0 5px 0 3px;
  font: bold 11px/18px 'Helvetica Neue',Arial,sans-serif;
  color: #333;
  text-decoration: none;
  text-shadow: 0 1px 0 rgba(255,255,255,0.5);
  white-space: nowrap;
  cursor: pointer;
  background-color: #eee;
  background-image: -webkit-linear-gradient(#fff,#dedede);
  background-image: linear-gradient(#fff,#EEAA88);
  border: #000000 solid 1px;
  border-radius: 3px;
}
html, .SandboxRoot, .p-author .profile .p-name, .custom-timeline-owner-profile .p-name, .cards-base p, .cards-base p a, .timeline-header .custom-timeline-summary, .timeline-header .custom-timeline-summary a:link, .timeline-header .custom-timeline-summary a:visited, .timeline-header .summary, .timeline-header .summary a:link, .timeline-header .summary a:visited, .p-author a.profile:hover .p-name, .p-author a.profile:focus .p-name {
  color: #292f33;
}
.tweet-box-button {
  display: block;
  width: 100%;
  padding: 6px;
  text-align: left;
  background: #FD7B5D;
  border: 1px #800000 solid;
  border-radius: 4px;
}
.timeline {
  margin-bottom: 10px;
  background-color: #FD7B5D;
  border-radius: 5px;
  border-color: #000;
}

body {
	background: #ffe;
	color: #800000;
	font-family: "Times New Roman", Times, serif;
	font-size: 16px;
}
div.title h1 {
	font-size: 24px;
}
div.title p {
	font-size: 10px;
}
a:link, a:visited, .intro a.email span.name {
	color: #0000ff;
}
a:link:hover {
	color: #d00;
}
a.post_no {
	color: #800000;
}
div.post.reply {
	background: #f0e0d6;
	border-color: #d9bfb7;
}
div.post.reply.highlighted {
	background: #f0c0b0;
	border-color: #d9bfb7;
}
div.post.reply div.body a {
	color: navy;
}
.intro span.subject {
	color: #d00;
}
form table tr th {
	background: #EA8;
}
div.ban h2 {
	background: #FCA;
	color: inherit;
}
div.ban {
	border-color: #800;
}
div.ban p {
	color: black;
}
div.pages {
	padding: 7px 5px;
	color: maroon;
	font-size: 12pt;
	
	background: none;
	border-width: 1px;
	border-style: inset;

}
div.pages a.selected {
	color: #800;
}
hr {
	border-width: 1px;
	border-style: inset;
}
div.boardlist {
	color: #B86;
}
div.boardlist a {
	color: #800;
}
unimportant, .unimportant * {
	font-size: 13px;
}
table.modlog tr th {
	background: #EA8;
}

.desktop-style div.boardlist:nth-child(1) {
  text-shadow: #fff 1px 1px 1px, #fff -1px -1px 1px;
}


.desktop-style div.boardlist:nth-child(1):hover, .desktop-style div.boardlist:nth-child(1).cb-menu {
  background-color: rgba(90%, 90%, 90%, 0.55);
}

/* options.js */
#options_div, #alert_div {
  background: rgb(240, 224, 214);
}
.options_tab_icon {
  border: 1px solid transparent;
  border-width: 0px 1px 1px 0px;
}
.options_tab_icon:hover {
  border-color: #d9bfb7;
  border-width: 0px 1px 1px 0px;
}
.options_tab_icon.active {
  border-color: #d9bfb7;
  border-width: 1px 0px 0px 1px;
}
}
