css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("habrahabr.ru"),
domain("geektimes.ru") {
    .sidebar_right,
    .column-wrapper.column-wrapper_bottom.column-wrapper_bordered,
    #footer,
    .for_users_only_msg,
    .html_banner_wrap,
    .similar-posts.similar-posts_full,
    .voting-wjt__button.voting-wjt__button_plus.js-plus,
    .voting-wjt__button.voting-wjt__button_minus.js-minus,
    .promo-block.promo-block_freelansim-tasks,
    .promo-block.promo-block_vacancies,
    .default-block.default-block_content,
    .post__footer,
    .post__flow,
    .post__title-arrow,
    .comment-item__avatar-img,
    .comment-item__avatar-img_default,
    .comment-item__avatar,
    .informer,
    .company_top_banner,
    .reply
    { display: none !important  }
    .tab-item {
        font-size: 20px
    }
    #navbar .nav_panel {
        background: #dedede
    }
    .voting-wjt__counter-score,
    .voting-wjt__result-score,
    .voting-wjt__button_score {
        font-size: 38px
    }
    .folding-dot {
        background: #bdbdbd;
        top: 20px;
        height: 3px
    }
    .voting-wjt_comments .voting-wjt__counter {
        line-height: 32px
    }
    .buttons a.button {
        font-size: 20px !important;
        margin-top: 20px !important
    }
    a {
        text-decoration: none;
        color: #007a9b
    }
    pre code {
        font-size: 18px
    }
    .post__time_published {
        color: #cb6419;
        font-size: 16px
    }
    .voting-wjt__button_score,
    .voting-wjt__counter-score,
    .voting-wjt__result-score {
        color: rgba(198, 212, 216, 0)
    }
    .voting-wjt_comments {
        float: left;
        padding-right: 24px !important
    }
    .fonts-loaded .post__flow,
    .fonts-loaded .post__title,
    .fonts-loaded .post__title_link {
        font-weight: 700 !important
    }
    .posts .i-am-your-father-luke,
    .posts .post {
        margin-bottom: 100px !important
    }
    .post .content {
        font-size: 18px !important
    }
    .comments_list .comment_item .message.bad1 {
        background: none !important
    }
    #layout,
    #layout .inner {
        background: none !important
    }
    .search-form {
        margin-left: 85px !important;
        height: 27px !important
    }
    .post__title_link {
        line-height: 40px
    }
    .page-header__title {
        font-size: 20px !important
    }

    .post__flow,
    .post__title,
    .post__title_link {
        font-size: 30px
    }
    .post .hubs {
        font-size: 16px;
        padding: 20px 0 20px 0
    }
    .icon_briefcase2::before,
    .post .hubs::before {
        content: none
    }
    .flag {
        font-size: 40px;
        line-height: initial !important;
        text-align: initial !important;
        padding: initial !important;
        border-top-left-radius: initial !important;
        border-bottom-left-radius: initial !important;
        position: initial !important;
        margin-right: initial !important;
        background-color: rgba(179, 213, 234, 0);
        color: rgba(152, 65, 222, 0.94)
    }
    .flag::after {
        content: none
    }
    .comments_list .comment_item .message {
        font-size: 18px;
        color: #5f4c4c
    }
    .page-nav #nav-pages {
        font-size: 24px;
        margin-bottom: 100px
    }
    .comment-item {
        height: 40px
    }
    .comment-item.is_topic_starter {
        background: #ededed
    }
    .comments_list .comment_item .message.bad1 {
        opacity: .3
    }
}
