@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kinogo.co"), domain("kinogo-net.ru") {
  
   html, body {background: rgba(0,0,0, .05)url(/*[[URL]]*/)!important; 
  background-color: #111 !important;
	background-attachment: fixed !important;
	background-clip: border-box !important;
	background-origin: padding-box !important;
	background-position: center center !important;
	background-repeat: no-repeat !important;
	background-size: cover !important;}  
  *{font-family:/*[[font]]*/!important}  
  
  
  /* background & border remower */	
	.contener, .shortstory, .icons, .content, .contener2, .fullstory, .relatednews_title, .relatednews, .ui-widget-header, .icons2, .tabs li.current, .oformlenie {background:transparent!important}  	
  *{border:none!important}  
	
	
	/* box & shadow */	
	.header_black {background: rgba(0, 0, 0,  0.8)!important;border-radius: 0px 0px 25px 25px !important; box-shadow: 0px 0px 8px rgba(0, 190, 255, .9 ) inset!important}  
  .horizontal {background: rgba(0, 0, 0,  0.8)!important;border-radius: 15px !important;box-shadow: 0px 0px 3px rgba(255, 255, 255, .9 ) inset!important}  
  table.menu tr td a, table.menu tr td input {background: rgba(0, 0, 0,  0.8)!important;border-radius: 5px !important}
  table.menu tr td a:hover, table.menu tr td input:hover {border-radius: 15px 0px !important;box-shadow: 0 0 3px #3cf inset!important}  
  .contener {background: rgba(0, 0, 0,  0.8)!important;border-radius: 20px !important}
  .shortstory{background: rgba(0, 190, 255,  0.03)!important;border-radius: 7px !important}
	.title_spoiler, .text_spoiler, .section, #orderdesc-table tbody tr.orderdescr-tr-odd, #orderdesc-table tbody tr:hover, #orderdesc-table thead tr, .quote {background: rgba(0, 190, 255,  0.08)!important;border-radius: 5px !important}
	.ui-widget-content {background: rgba(0, 0, 0,  0.68)!important;box-shadow: 0 0 3px #3cf!important}	
	.footer {background: rgba(0, 0, 0,  0.8)!important;;border-radius: 0px 0px 20px 20px !important}   
  
  
  /* search & textarea */
  .header44 .search_panel input, .header44 .user_panel input, input, textarea, select {background: rgba(0, 0, 0, 0.25)!important; color: #3ce!important;box-shadow: 0px 0px 3px rgba(0, 215, 255, .9) inset!important;border: none !important;border-radius:5px!important;padding:6px!important}  
  .header44 .search_panel input:hover, .header44 .user_panel input:hover, input:hover, textarea:hover, select:hover {box-shadow: 0px 0px 8px rgba(0, 215, 255, .9) inset!important}  
	
	
	/* button */  
  .fbutton2, .fbutton-img, span.fbutton-img, .fbutton5, span.fbutton5, .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button, .ui-widget, #orderdesc-navigation span, #orderdesc-navigation a, .pluso-wrap div, .fbutton, span.fbutton {background: rgba(0, 0, 0,  0.7)!important;border-radius: 5px !important;border:none!important;color:#3ce!important;outline:none!important}  
  .fbutton2:hover, .fbutton-img:hover, span.fbutton-img:hover, .fbutton5:hover, span.fbutton5:hover, .ui-widget input:hover, .ui-widget select:hover, .ui-widget textarea:hover, .ui-widget button:hover, .ui-widget:hover, #orderdesc-navigation span:hover, #orderdesc-navigation a:hover, .pluso-wrap div:hover, .fbutton:hover, span.fbutton:hover, .bbcode-editor .bbcode:hover {box-shadow: 0 0 3px #3cf!important} 
	.tabs li:hover {background:transparent!important;box-shadow: 0 0 3px #3cf!important;text-shadow:none!important}
	.bbcode-editor .bbcode {background-color:rgba(0, 0, 0,  0.7)!important;border-radius:5px!important}
	
	
	/* text color & decoration */  
  html, body, a,  span:hover {color:#3ce!important;text-decoration:none!important}  
  a:hover {color:#39e!important;text-decoration:none!important} 	
	div, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, abbr, address, cite, code, del, dfn, em, img, ins, kbd, q, samp, small, strong, sub, sup, var, b, i, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, figcaption, figure, footer, header, hgroup, menu, nav, section, summary, time, mark, audio, video, button, select {color:#59e!important;text-decoration:none!important}
	
  
  /* ico */		
	body {background: url(/*[[logo]]*/) no-repeat scroll transparent !important; background-size: /*[[size]]*/% !important;background-position: top !important}
  .header44 .main_menu {background: url(http://savepic.org/7219733.png) no-repeat scroll transparent !important; background-size: contain !important;margin-top:12px!important;margin-right:10px!important}
  
  
  /* opacity */	
	.li_block_rmb, .social-likes {opacity:.1!important}
	.li_block_rmb:hover, .social-likes:hover {opacity:.9!important}
	.main_menu>a>img {opacity:.04!important}   
    
  
  /* ads */  
  #skin_block2d49e110, #skin_block35fe9e80, #skin_block4de6eb61, body>noindex:nth-child(1), .rek_top, .mctable, .li_block_bn, #customwl_1398208637, .icons3, #bn_4a651137d0, #bn_28b8834e3f, .box > center:nth-child(1) > div:nth-child(1), #skin_block4ef6a5c0, .logo_header>a>img {display:none!important}}
