css:
@-moz-document domain("deviantart.com") {
/*
sun-bliss - deviantArt
DeviantART pastel skin.
Deviantart with pastel and muted colours for a more colourful non-destructive browsing of your favourite artists.
This is a modified version of LilyB's skin found here: https://userstyles.org/styles/102172/lilyb-s-deviantart
*/
/*persistent things*/
body{
	background:#1E457E!important;
	color:#edf5dd!important;
	text-shadow:1px 1px 1px #1F261F!important}

#content{
	color:#2d5ea3!important;
	text-shadow:1px 1px 1px #1F261F!important}

#overhead{
	background: linear-gradient(#2d7dbb,#3a965a)!important;
	color:#2d5ea3!important;
border-bottom:1px solid #fcf400!important;
box-shadow:2px 0px 0px #844d60!important 
}

#overhead a{
	color:#cc002f!important}

#overhead #search7 input[name="q"],
#overhead .ntfirst,
#overhead .ntmid,
#overhead .ntlast{
	color:#cc002f!important}

#overhead .ntfirst.disabled,
#overhead .ntmid.disabled,
#overhead .ntlast.disabled{
	color:#BBBBBB!important}
	
#oh-menu-submit>a>span{
	background:none!important;
	font-weight:bold!important;
	color:#28d364!important;
	padding:7px 0px!important;
	box-shadow:none!important}
	
#oh-menu-submit a:hover,
#oh-menu-upgrade a:hover,
#overhead #oh-menu-friends:hover,
#overhead #oh-menu-collect:hover,
#overhead .retina_logo:hover{
	background:#3c4441!important}

#oh-menu-submit .oh-menu{
	left:-10px !important}

body.deviantart #overhead .oh-gap+td:not(#oh-menu-readonly){
	border-left:10px solid #fcf400!important;
}

#oh-menu-join,
#oh-loginbutton,
#oh-menu-deviant,
#oh-menu-split,
#oh-menu-friends,
#oh-menu-collect,
#oh-menu-cart,
#overhead .oh-mc-split,
#oh-menu-greeting{
	background: linear-gradient(#127235,#0a6292)!important}
	
.oh-menu a.active,
.oh-menu a.selected,
.mi:hover,
.mi:focus{
	background:#3c4441!important}


dl.deviants{
	color:#1F261F!important;
	text-shadow:none!important}

body .friendmachine .controls a.edit{
	background:#62cbe0!important;
	text-shadow:none!important}

body .friendmachine .controls a.edit:hover{
	background:#61745B!important}

.friendmachine .readout{
	background:#2D3735!important}

#navbar-menu>.navbar-menu-inner{
	background:#b73560!important}

#navbar-menu .jump-to-top {
  color: #edf5dd;
  cursor: pointer;
  display: none;
  font: 14px "Helvetica Neue",Arial,sans-serif;
  height: 35px;
  letter-spacing: normal;
  line-height: 35px;
  padding: 0 10px 0 0;
  position: absolute;
  right: 0;
  top: 0;
}
	
#navbar-menu a{
	color:#62cbe0!important}

#navbar-menu a:hover,
#navbar-menu li.hovered a{
	color:#31859d!important}

#navbar-menu a.active,
#navbar-menu li.hovered a.active{
	color:#fcf400!important}
	
#depths,
#depths .depths-inner{
            background: linear-gradient(#3a965a,#2d7dbb)!important;
	color:#2d5ea3!important
	}

#depths a{
	color:#844d60!important}

#depths a:hover{
	color:#6d2940!important}

/*related pages*/
.login-page #login{
	background:#CCCCCC!important;
	text-shadow:none!important}

.login-page .error .error-message{
	text-shadow:none!important}

/*redirect page*//*deprecated*/
#world{
	position:relative!important;
	top:-90px!important;
	background:#085077!important}
	
#world img{
	display:none!important}
	
#world td{
	background-color:#085077!important}
	
#world h2{
	color:#CFCA93!important}
	
#world .gr-body{
	color:#2d5ea3!important}
/*end redirect page*/
	
/*buttons*/
.smbutton,
.smbutton-pale,
.gmbutton2{
	color:#2d7dbb!important;
	text-shadow:none!important}

.smbutton-green,
.ccexpanded .smbutton-big span{
	background:#83b7be!important;
	color:#2d5ea3!important;
	text-shadow:1px 1px 1px #1F261F!important;
	box-shadow:none!important}

.smbutton-green:hover,
.ccexpanded .smbutton-big span:hover{
	background:#61745B!important}

.gmbutton2f{
	color:#2d5ea3!important}

.gmbutton2qn2r span,
.gmbutton2qn2r b span{
	color:#1F261F!important}
/*end buttons*/
/*end persistent things*/


/*applicable all pages*/
#output,
.bubbleview{
	background:#085077!important}

body.gruze .bubbleview,
.policy-page p{
	color:#69ddf4!important}

/*special case.!NOT IMPORTANT, this fixes the color of most of the links*/
:link{
	color:#0a6292}

:link:hover{
	color:#62cbe0}

h1,
h1 a,
h2{
	color:#2d7dbb}

a.u:not(.banned),
body.gruze a.u:not(.banned),
.mcb-title a:not(.external),
.mcb-line a:not(.external),
.mcb-body a:not(.external),
.mcb-modhtml a:not(.external),
.popup2-mcbox-comment a:not(.external):not(.thumb),
.text a:not(.external),
a.external,
body.gruze a,
.text a,
body.gruze div.bubbleview a.a,
body.gruze div.bubbleview div.alink a{
	color:#2d7dbb}

a.u:not(.banned):hover,
body.gruze a.u:not(.banned):hover,
.mcb-title a:not(.external):hover,
.mcb-line a:not(.external):hover,
.mcb-body a:not(.external):hover,
.mcb-modhtml a:not(.external):hover,
.popup2-mcbox-comment a:not(.external):not(.thumb):hover,
.text a:not(.external):hover,
a.external:hover,
body.gruze a:hover,
.text a:hover,
body.gruze div.bubbleview a.a:hover,
body.gruze div.bubbleview div.alink a:hover{
	color:#62cbe0}

.gtabi-gm, .gtabi-gm .gtab-i {
  font-weight: bold;
  color: #1e457e !important;
  cursor: default;
}

a.lit q{
	color:#1F261F!important;
	text-shadow:none!important}

.browse-inline-ad{
	display:none!important}

body.gruze ul.list li.a,
body.gruze div.gr-box div.gr-body table.grf tr.a{
	background:#666666!important}

.featuredtags-widget{
	color:#2d5ea3!important}

.featuredtags-widget a em{
	color:#127235!important}

.featuredtags-widget a,
.featuredtags-widget a em:hover{
	color:#1faf43!important}

/*pagination*/
.pagination ul.pages li a{
	color:#61745B!important}

.pagination ul.pages li.current a{
