.pagination ul.pages li.dotdotdot a:hover{
	background-color:#62cbe0!important;
	border-color:#61745B!important}

.pagination ul.pages li.prev a.disabled,
.pagination ul.pages li.next a.disabled{
	color:#BBBBBB!important}

.browse-results-page .showing-page,
.paging-results,
.paging-results-popup-content{
