.modalFooter .upsell a.smbutton span{
	text-shadow:1px 1px 1px #1F261F!important}
/*end symbols and pop-up*/

/*catbars*/
body.gruze .catbar,
.catbar,
.bubbletop,
.bubbleview .catbar{
