.gr-top i.tri{
	background:url('http://s25.postimg.org/f6hcxm4tn/gr_top_ar.gif') no-repeat!important}

.gr-body .gr{
	background:url('https://farm3.staticflickr.com/2844/9135832595_56838a807f_b.jpg')!important;
           background-size: 100%;
           background-repeat: no-repeat;
           background-attachment: fixed;
           background-position: center;
	} 

.gr-box .mem-display .rolelabel{
