.gr-body i.c{
	border-color:red!important}/*i have never seen this since the update. i hope to see them eventually or i will remove them*/

.gr-body i.c b{
	background:red!important}
/*end .gr stuff*/

/*comment speech bubbles, comment inputs*/
.ccomment .ch-ctrl .ch-ctrl{
	background:#83b7be!important;
	border-color:#222222!important}

.cctextarea i.l{
	background:url('http://s25.postimg.org/mkzvygdm3/aac_Speak.gif') no-repeat!important}

.ccomment i.l{
	background:url('http://s25.postimg.org/9sbryj20b/speak.gif') no-repeat!important}

