.cctextarea{
	background:
		url('http://s25.postimg.org/53vs3cctn/textarea_shadow_left.gif') no-repeat scroll 0% 0%,
		url('http://s25.postimg.org/cl4zik2cr/textarea_shadow_right.gif') no-repeat scroll right top,
		url('http://s25.postimg.org/5s4mmabjf/textarea_shadow.gif')      repeat-x scroll 0% 0%
		#999999;
	border-color:#222222!important;
	box-shadow:none!important}

.cctextarea textarea,
.previewbox {
	background-color:#999999!important}

div.ccwriter div.writer,
div.ccwriter-wrapper div.writer{
	color:#1F261F!important;
	text-shadow:none!important}

.cctextarea-ctrl{
	color:#844d60!important;
	text-shadow:none!important}
/*end comment speech bubbles*/
/*end applicable all pages*/


/*main pages*/
/*today*/
.today-page .shinyheader{
	background:linear-gradient(#127235,#222222)!important;
	border:100px solid #127235!important;
	box-shadow:none!important}

