css:
@-moz-document regexp("https?://(www\\.)?wanikani.com/.*") {
body:not(.wysihtml5-editor) {
    background: url(data:image/png;base64,);
    color: #dadada;
  }
  p, ol{
    text-shadow: none;
  }
 footer{
    background-image: none;
  }
  a {
    color: #ddd !important;
    text-shadow: none !important;
  }
  /* Pagers */
  .page-list {
    background-color: rgba(0, 0, 0, 0.27);
    border-radius: 5px
  }
  .page-list ul > li.page-list-header > span{
    text-shadow: none;
  }
  .page-list ul > li > a{
    background-color: #333;
    text-shadow: none;
    color: #ddd;
    border: 1px solid #222;
    margin: 3px;
  }
  /* Dashboard */
  .dashboard section.review-status{
    color:#ddd;
    text-shadow: none;
  }
  .navbar .nav > li > a{
    text-shadow: none;
  }
  .navbar-inner{
    background-image: linear-gradient(#232323, #1f1f1f) !important;
    border-bottom-color: #111 !important;
  }
  html#main .navbar.scrolled .navbar-inner {
    background-image: linear-gradient(#232323, #1f1f1f);
  }
  html#main .navbar .nav > li:not(.lessons):not(.reviews) > a > span:first-child, html#public-profile .navbar .nav > li:not(.lessons):not(.reviews) > a > span:first-child {
    box-shadow: none;
  }
  .dashboard section.progression{
    background-color: #282828;
  }
  .dashboard section.review-status ul li {
    border-right-color: #666;
    border-left: #666;
  }
  h1, html#public-profile div.wall-of-shame div.title, h2, h3{
    text-shadow: none;
  }
  .progression h3{
    color: #ddd;
  }
  .small-caps {
    text-shadow: none;
  }
  .progress .threshold {
    text-shadow: none;
    color: #222;
  }
  .popover .popover-title {
    background-color: #333 !important;
  }
  div.chart {
    text-shadow: none;
  }
  .dashboard-sub-section tr {
    background-color: #333 !important;
  }
  .dashboard-sub-section .small-caps {
    background-color: #333 !important;
    color: #ddd !important;
  }
  tr.none-available {
    text-shadow: none !important;
    color: #ddd !important;
  }
  tr.none-available div {
    box-shadow: none !important;
  }
  .forum-topics-list table td {
    text-shadow: none;
  }
  .forum-topics-list a.topic-title{
    color: #ddd;
  }
  .forum-topics-list a {
    color: #bababa;
  }
  /* Summary */
  #reviews-summary #review-stats [id*="review-stats-"], #reviews-summary #incorrect, #reviews-summary #correct, #lessons-summary #vocabulary, #lessons-summary #kanji, #lessons-summary #radicals {
    background-color: #2a2a2a;
    box-shadow: none;
    border: 1px solid #1f1f1f;
  }
  li.vocabulary, li.radicals, li.kanji{
    box-shadow: none !important;
  }
  #reviews-summary #incorrect h2, #reviews-summary #correct h2{
    text-shadow: none;
  }
  .active h3 span{
    background-color: #2a2a2a !important;
  }
  .active h3 strong{
    color: #222 !important;
  }
  /* Questions */
  #loading, #loading-screen {
    background-color: #333;
  }
  #question-type{
    background-color: #333 !important;
    background-image: none !important;
    border-bottom-color: #222 !important;
    border-top-color: #222 !important;
    color: #ddd !important;
  }
  #additional-content ul li span, #lesson #supplement-info{
    background-color: #333;
    box-shadow: none;
    border-radius: 5px;
  }
  #answer-form input[type="text"]{
    box-shadow: none;
    background-color: #333;
    color: #ddd;
    border-radius: 5px;
  }
  #answer-form button{
    background-color: #222;
    border-radius: 0 5px 5px 0;
  }
  #item-info blockquote, #information, #lesson #supplement-info blockquote{
    background-color: #222;
    border-radius: 5px;
    box-shadow: none;
    color: #ddd;
  }
  [class*="highlight-"]{
    text-shadow: none !important;
  }
  .highlight-vocabulary{
    background-color: #8000A3;
  }
  .highlight-kanji{
    background-color: #993D7A;
  }
  .highlight-radical{
    background-color: #597280;
  }
  #item-info #related-items ul span{
    box-shadow: none;
    text-shadow: none;
  }
  #item-info #all-info{
    background-color: #222;
    border-radius: 5px;
  }
  #additional-content ul li:not(#option-audio) span:hover::before{
    background-color: #2a2a2a;
    box-shadow: none;
    border-radius: 5px;
  }
  #lesson #supplement-info .kanji{
    box-shadow: none;
  }
  #answer-exception span{
    box-shadow: none;
    border-radius: 5px;
    background-color: #2a2a2a;
  }
  #answer-exception span::before {
   border-color: transparent transparent #2a2a2a;
  }
  #additional-content ul li.active::before{
    border-color: transparent transparent #333;
  }
  #answer-form fieldset.correct button, #answer-form fieldset.correct input[type="text"], #answer-form fieldset.correct input[type="text"]:disabled{
    background-color: #6a0 !important;
  }
   #lesson #supplement-info #prev-btn:hover, #lesson #supplement-info #next-btn:hover {
     background-color: #282828;
     border-radius: 0 5px 5px 0;
   }
  /* Profile / Settings */
  html#main .additional-info, html#public-profile .additional-info{
    background-color: #282828;
    border-radius: 5px
  }
  html#public-profile div.wall-of-shame{
    background-color: rgba(0, 0, 0, 0.27);
  }
  html#public-profile div.wall-of-shame ul li * {
    text-shadow: none !important;
  }
  .progress .bar span{
    color: #222;
    text-shadow: none;
  }
  #additional-content-load {
    background-color: rgba(0, 0, 0, 0.27);
  }
  #item-info #related-items a, #item-info #related-items a:active, #item-info #related-items a:hover, #item-info #related-items a:visited{
    color: #dadada;
  }
  .forum-header-bg::before, .forum-list li::before{
    background-color: #222;
  }
  .forum-list p, .forum-list h3, .additional-info h3 {
    text-shadow: none;
    color: #ddd;
  }
}
