css:
@-moz-document domain("www.toonbook.me") {
 
*{-moz-border-radius: 6px !important;  
cursor:url("http://www.snazzyspace.com/cursorsfolder/crown.png"), default !important;
color: white !important;
text-shadow: -1px 0 #B5CEF2, 0 1px #B5CEF2, 1px 0 #B5CEF2, 0 -1px #B5CEF2;}
  
body {
background-image:url("http://www.ipicstorage.com/wp-content/uploads/Uta-no-Prince-Sama-290834-EiraDicra.jpg") !important;
background-size:cover !important;
background-repeat:repeat !important;
background-attachment:fixed !important;
background-position:center !important; }
  
.wall-broadcast-item {
background-color: #BFD9FF; }
  
.comments > ul > li {
background-color: #B5CEF2; }  
  
ul.feed > li {
border-bottom: 1px solid #B5CEF2; }  
  
ul.feed .feed_item_attachments {
background-color: #BFD9FF; }  
  
#global_content {
border-right: 0px; }  

a.buttonlink {
border-bottom: 1px solid #B5CEF2; 
background-color:#BFD9FF; }
  
#global_header {
background:#CC6666;
border-bottom:transparent; }  
  
#arrowchat_buddy_list_tab {
background:#CC6666; }  
  
.layout_announcement_list_announcements {
border-left: 0px; }  
 
h3 {
background-color: #CC6666;
border-top: 1px solid #CC6666;}  
  
.feed_item_broadcast {
background-image: url("http://icons.iconarchive.com/icons/salleedesign/music/16/microphone-foam-blue-icon.png") !important; }  
  
.layout_wall_feed {
border-left: 0px; }  

.sd_pulldown_header {
background-color:#CC6666;
font-size: 1.3em;  
font-family: CatholicSchoolGirls BB; }  

.sd_pulldown_contents {
background:#BFD9FF; }  
  
.pulldown_options {
background-color: #CC6666;
font-family: CatholicSchoolGirls BB;
font-size: 1.3em;}
  
.arrowchat_tabpopup {
background-color: #FFF;
font-family: CatholicSchoolGirls BB; }  
 
.arrowchat_powered_by {
border-right: 1px solid #B2B2B2;
border-left: 1px solid #B2B2B2;
border-bottom: 1px solid #C9D0DA;
background-color: #BFD9FF; } 
  
.arrowchat_userstabtitle {
background-color: #CC6666; 
border-color:#CC6666; }
  
.arrowchat_bar_button {
background:#CC6666;
border-right: 0px;
border-left: 0px; } 
  
.arrowchat_tabcontent {
border-right: 1px solid #B2B2B2;
border-left: 1px solid #B2B2B2;
background-color: #B5CEF2; } 
  
.arrowchat_userlist {
border-top: 1px solid #BFD9FF;
border-bottom: 1px solid #BFD9FF; }    
  
#global_page_timeline-profile-index #global_content .tli {
border: 1px solid #B5CEF2;
background-color: #BFD9FF; }  
  
#global_page_timeline-profile-index #global_content .tl-block {
background-color: #BFD9FF;
border-right: 0px;
border-image: none;
border-color: #B5CEF2; }  
  
ul.feed > li:first-child {
border-top: 0px;}  

.wall-menu-expand-link {
border: 1px solid #B5CEF2; }
  
#global_page_wall-index-view .layout_middle {
border-left: 0px; }
  
#TBsu_topbar {
background-color: #A0727D; }
  
#TBsu_wrapper {
background-image:url("http://images6.fanpop.com/image/photos/34700000/UtaPri-Wallpaper-uta-no-prince-sama-34729694-1100-771.jpg") !important; 
background-size:cover  !important;
background-repeat:repeat !important;  
background-attachment: unset !important;
background-position:center !important; }    
  
#TBsu_footer {
display:none; }  
  
.TBsu_ftrs {
display: none; }  
  
.global_form {
display: none;}  
  
.generic_layout_container h3 {
display: none; }
  
center {
display: none !important; }  
  
.layout_core_statistics ul, .layout_activity_list_requests ul {
display: none; }
  
a.buttonlink {
display: none; }  
  
.links_nav {
display: none; }  
  
.layout_user_list_signups > ul > li {
display: none; } 

.toonbook_debug_info {
display: none; }  
    
}
